import { defineConfig, devices } from "@playwright/test";

const BASE_URL =
  process.env.BASE_URL || `http://localhost:${process.env.PORT || 5000}`;

export default defineConfig({
  testDir: "tests",
  testMatch: "**/*.spec.ts", // Only run .spec.ts files (exclude .test.ts files for Vitest)
  timeout: 60_000,
  expect: { timeout: 5_000 },
  retries: 0,
  use: {
    baseURL: BASE_URL,
    trace: "on-first-retry",
    screenshot: "only-on-failure",
    video: "off",
  },
  projects: [{ name: "chromium", use: { ...devices["Desktop Chrome"] } }],
  reporter: [
    ["html", { outputFolder: "logs/playwright-report" }],
    ["json", { outputFile: "logs/test-results.json" }],
  ],
  // CRITICAL: Start server before tests and wait for health endpoint
  webServer: {
    command: "npm run dev",
    timeout: 30_000, // 30 seconds to start
    reuseExistingServer: !process.env.CI, // Reuse server in development
    env: {
      NODE_ENV: "development",
      AUTH_ENABLED: "false", // Disable auth for tests
    },
    // Wait for health endpoint to return ready=true
    url: `${BASE_URL}/api/health`,
    // Custom wait logic to ensure server is truly ready
    async check(url) {
      try {
        const response = await fetch(url);
        if (response.ok) {
          const data = await response.json();
          console.log("Playwright server check:", data);
          // Wait for ready=true, not just 200 status
          return data.ready === true;
        }
        return false;
      } catch (error) {
        console.log("Playwright server check error:", error.message);
        return false;
      }
    },
  },
});

