const app = express();
const port = process.env.PORT || 5000; // Replit-Standard Port

// 1. Pool fÃ¼r die Datenbankverbindung erstellen
// Die DATABASE_URL sollte in Ihren Replit Secrets gespeichert sein.
const pool = new pg.Pool({
  connectionString: process.env.DATABASE_URL,
  ssl: process.env.NODE_ENV === 'production' ? {
    rejectUnauthorized: false // Nur fÃ¼r Cloud-Datenbanken in Produktion
  } : false // Lokale Entwicklung ohne SSL
});

// 2. Session Store mit connect-pg-simple initialisieren
const PGStore = connectPgSimple(session);
const sessionStore = new PGStore({
  pool: pool,
  tableName: 'user_sessions', // Name der Tabelle fÃ¼r die Sitzungen
  createTableIfMissing: true // Automatische Tabellenerstellung
});

// 3. Express-Session-Middleware mit dem neuen Store konfigurieren
app.use(session({
  store: sessionStore,
  secret: process.env.SESSION_SECRET || 'ein_sehr_sicheres_geheimnis_fuer_die_entwicklung', // SESSION_SECRET MUSS in den Secrets sein!
  resave: false,
  saveUninitialized: false,
  name: 'bodensee.sid', // Eindeutiger Session-Name
  cookie: {
    maxAge: 30 * 24 * 60 * 60 * 1000, // 30 Tage
    secure: process.env.NODE_ENV === 'production', // Cookie nur Ã¼ber HTTPS senden
    httpOnly: true, // Verhindert Zugriff Ã¼ber clientseitiges JavaScript
    sameSite: process.env.NODE_ENV === 'production' ? 'strict' : 'lax' // StÃ¤rkerer Schutz in Produktion
  }
}));

