const sharp = require('sharp');

async function createTransparentLogo() {
  try {
    // Ersten Versuch: Basis Transparent-Konvertierung
    await sharp('client/src/assets/new-logo.png')
      .png({
        palette: false,
        compressionLevel: 9,
        adaptiveFiltering: true,
        force: true
      })
      .ensureAlpha()
      .raw()
      .toBuffer({ resolveWithObject: true })
      .then(async ({ data, info }) => {
        // Pixel-basierte Transparenz-Bearbeitung
        const { width, height, channels } = info;

        for (let i = 0; i < data.length; i += channels) {
          const r = data[i];
          const g = data[i + 1];
          const b = data[i + 2];

          // WeiÃŸe und sehr helle Pixel transparent machen
          if (r > 240 && g > 240 && b > 240) {
            data[i + 3] = 0; // Alpha auf 0 setzen (transparent)
          }
          // Leicht graue Pixel auch transparent machen
          else if (r > 220 && g > 220 && b > 220 && Math.abs(r - g) < 10 && Math.abs(g - b) < 10) {
            data[i + 3] = 0;
          }
        }

        return sharp(data, { raw: { width, height, channels } })
          .png()
          .toFile('client/src/assets/new-logo-transparent.png');
      });

    console.log('âœ… Transparentes Logo erfolgreich erstellt!');

    // Info Ã¼ber das erstellte Bild
    const metadata = await sharp('client/src/assets/new-logo-transparent.png').metadata();
    console.log('ğŸ“ Logo-Dimensionen:', metadata.width + 'x' + metadata.height);
    console.log('ğŸ¨ KanÃ¤le:', metadata.channels);
    console.log('ğŸ“ Pfad: client/src/assets/new-logo-transparent.png');

  } catch (error) {
    console.error('âŒ Fehler beim Erstellen des transparenten Logos:', error);
  }
}

createTransparentLogo();

