const sharp = require('sharp');
const fs = require('fs');
const path = require('path');

async function resizeLogo() {
  try {
    const inputPath = path.join(process.cwd(), 'attached_assets');
    const outputPath = path.join(process.cwd(), 'client/src/assets/logo-am.png');

    // Finde das neueste hochgeladene Bild
    const files = fs.readdirSync(inputPath);
    const logoFile = files.find(file => file.includes('logo') && file.includes('mm'));

    if (!logoFile) {
      console.error('Logo-Datei nicht gefunden');
      return;
    }

    const inputFullPath = path.join(inputPath, logoFile);

    // Logo auf optimale GrÃ¶ÃŸe fÃ¼r Web verkleinern (max 200px Breite)
    await sharp(inputFullPath)
      .resize(200, null, {
        withoutEnlargement: true,
        fit: 'inside'
      })
      .png({
        quality: 90,
        compressionLevel: 9
      })
      .toFile(outputPath);

    console.log('Logo erfolgreich verkleinert und gespeichert:', outputPath);

    // Backup des alten Logos erstellen
    const backupPath = path.join(process.cwd(), 'client/src/assets/logo-am-backup.png');
    if (fs.existsSync(outputPath)) {
      fs.copyFileSync(outputPath, backupPath);
    }

  } catch (error) {
    console.error('Fehler beim Verkleinern des Logos:', error);
  }
}

resizeLogo();

