const distServerDir = join(rootDir, 'dist', 'server');
if (!existsSync(distServerDir)) {
  mkdirSync(distServerDir, { recursive: true });
}

// Schritt 4: Erstelle Entry Point fÃ¼r Deployment
const entryPointPath = join(distServerDir, 'index.js');
const entryPointContent = `#!/usr/bin/env node

/**
 * DEPLOYMENT ENTRY POINT
 * Auto-generiert vom Build-Prozess
 */

import { existsSync } from 'fs';
