import { dirname, join } from 'path';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

// PrÃ¼fe ob kompilierte Server-Dateien existieren
const compiledServerPath = join(__dirname, 'server.js');
const hasCompiledServer = existsSync(compiledServerPath);

if (hasCompiledServer) {
  console.log('ğŸ“¦ Starting with compiled JavaScript server...');
  console.log('Port:', process.env.PORT || 5000);
  // Importiere und starte den kompilierten Server
  await import('./server.js');
} else {
  console.log('ğŸ“ Starting with TypeScript runtime (tsx)...');
  console.log('Port:', process.env.PORT || 5000);

  // Nutze tsx als Fallback
  const { execSync } = await import('child_process');

