#!/usr/bin/env node

/**
 * DEPLOYMENT FIX - APPLIES ALL SUGGESTED FIXES
 * 
 * This script implements all the suggested fixes for deployment:
 * 1. Fix build command to use correct Vite build command from client directory
 * 2. Fix start command to use tsx runtime instead of compiled JavaScript
 * 3. Add proper server build script (using tsx runtime)
 * 4. Update run command to use tsx runtime for TypeScript execution
 */

import { execSync } from 'child_process';
import fs from 'fs';
import path from 'path';

console.log('ğŸš€ DEPLOYMENT FIX - Applying all suggested fixes');
console.log('=====================================================');

try {
  // 1. Fix the build command to use correct Vite build command from client directory
  console.log('ğŸ“¦ Building client with correct Vite build command...');
  console.log('   Using: cd client && npx vite build');

  // Change to client directory and run vite build
  process.chdir('client');
  execSync('npx vite build', { stdio: 'inherit' });
  process.chdir('..');

  console.log('âœ… Client build completed with correct build command');

  // 2. Verify the client build output location
  const clientDistPath = path.join('client', 'dist');
  const viteBuildPath = path.join('dist', 'public');

  if (fs.existsSync(clientDistPath) && fs.readdirSync(clientDistPath).length > 0) {
    console.log('âœ… Client build output found in client/dist');

    // Ensure dist/public exists for deployment compatibility
    fs.mkdirSync(path.dirname(viteBuildPath), { recursive: true });
    if (!fs.existsSync(viteBuildPath) || fs.readdirSync(viteBuildPath).length === 0) {
      console.log('ğŸ“‹ Copying client build to dist/public for deployment compatibility...');
      execSync(`cp -r ${clientDistPath}/* ${viteBuildPath}/`, { stdio: 'inherit' });
    }
  } else {
    throw new Error('Client build failed - no output found in client/dist');
  }

  // 3. Create deployment start script using tsx runtime (no compilation needed)
  console.log('ğŸ”§ Creating deployment start script using tsx runtime...');

  const deploymentStartScript = `#!/usr/bin/env node
/**
 * DEPLOYMENT START SCRIPT - TSX RUNTIME APPROACH
 * 
 * This script uses tsx runtime for TypeScript execution instead of 
 * compiling to JavaScript, which avoids compilation errors and 
 * is the recommended approach per the suggested fixes.
 */

import { execSync } from 'child_process';

console.log('ğŸš€ Starting production server with tsx runtime...');
console.log('ğŸ“ Using tsx runtime approach (no compilation required)');

// Set production environment
process.env.NODE_ENV = 'production';
process.env.HOST = '0.0.0.0';

try {
  // Start server using tsx runtime directly
  execSync('npx tsx server/index.ts', { 
    stdio: 'inherit',
    env: { 
      ...process.env, 
      NODE_ENV: 'production',
      HOST: '0.0.0.0'
    }
  });
} catch (error) {
  console.error('âŒ Production server failed:', error.message);
  process.exit(1);
}`;

  // Create dist directory and write the deployment start script
  fs.mkdirSync('dist', { recursive: true });
  fs.writeFileSync('dist/start-production.js', deploymentStartScript);
  console.log('âœ… Deployment start script created using tsx runtime');

  // 4. Create a comprehensive deployment wrapper script
  const deploymentWrapperScript = `#!/usr/bin/env node
/**
 * DEPLOYMENT WRAPPER - COMPLETE SOLUTION
 * 
 * This script applies all deployment fixes:
 * - Uses correct build commands
 * - Uses tsx runtime instead of compilation
 * - Provides proper deployment structure
 */

import { execSync } from 'child_process';

console.log('ğŸš€ DEPLOYMENT: Starting with all fixes applied');

// Apply all fixes and start production server
try {
  // Ensure we're using tsx runtime approach
  process.env.NODE_ENV = 'production';
  process.env.HOST = '0.0.0.0';

  console.log('ğŸ”§ Starting server with tsx runtime (deployment fix)...');
  execSync('npx tsx server/index.ts', { 
    stdio: 'inherit',
    env: { 
      ...process.env, 
      NODE_ENV: 'production',
      HOST: '0.0.0.0'
    }
  });
} catch (error) {
  console.error('âŒ Deployment failed:', error.message);
  process.exit(1);
}`;

  fs.writeFileSync('dist/deployment-wrapper.js', deploymentWrapperScript);
  console.log('âœ… Deployment wrapper script created');

  // 5. Verify deployment readiness
  console.log('\nğŸ“Š Deployment Readiness Check:');
  const checks = {
    'client/dist/index.html': fs.existsSync('client/dist/index.html'),
    'dist/public/index.html': fs.existsSync('dist/public/index.html'),
    'dist/start-production.js': fs.existsSync('dist/start-production.js'),
    'dist/deployment-wrapper.js': fs.existsSync('dist/deployment-wrapper.js'),
    'server/index.ts (source)': fs.existsSync('server/index.ts'),
    'vite.config.ts': fs.existsSync('vite.config.ts'),
    'package.json': fs.existsSync('package.json')
  };

  for (const [file, exists] of Object.entries(checks)) {
    console.log(`  ${exists ? 'âœ…' : 'âŒ'} ${file}: ${exists ? 'READY' : 'MISSING'}`);
  }

  const allReady = Object.values(checks).every(Boolean);

  if (allReady) {
    console.log('\nğŸ‰ ALL DEPLOYMENT FIXES APPLIED SUCCESSFULLY!');
    console.log('âœ… Fix 1: Build command uses correct Vite build from client directory');
    console.log('âœ… Fix 2: Start command uses tsx runtime instead of compiled JavaScript');
    console.log('âœ… Fix 3: Server build script uses tsx runtime (no compilation needed)');
    console.log('âœ… Fix 4: Run command uses tsx runtime for TypeScript execution');
    console.log('\nğŸš€ Deployment Options:');
    console.log('   â€¢ node dist/start-production.js (tsx runtime start)');
    console.log('   â€¢ node dist/deployment-wrapper.js (complete deployment)');
    console.log('   â€¢ NODE_ENV=production npx tsx server/index.ts (direct)');
    console.log('\nğŸ’¡ For .replit file deployment section:');
    console.log('   build = ["sh", "-c", "node scripts/deployment-fix.js"]');
    console.log('   run = ["sh", "-c", "NODE_ENV=production HOST=0.0.0.0 npx tsx server/index.ts"]');
    console.log('\nğŸ’¡ For package.json scripts (if accessible):');
    console.log('   "build": "cd client && npx vite build"');
    console.log('   "start": "NODE_ENV=production npx tsx server/index.ts"');
  } else {
    const missing = Object.entries(checks)
      .filter(([, exists]) => !exists)
      .map(([file]) => file);
    throw new Error(`Deployment verification failed. Missing: ${missing.join(', ')}`);
  }

} catch (error) {
  console.error('âŒ Deployment fix failed:', error.message);
  process.exit(1);
}

