#!/usr/bin/env node
/**
 * Utility script to generate secure authentication configuration
 * Generates ADMIN_PASSWORD_HASH, SESSION_SECRET, and provides setup instructions
 */

import crypto from 'crypto';
import { hashPassword } from '../server/lib/crypto.js';
import readline from 'readline';

// Create interface for user input
const rl = readline.createInterface({
  input: process.stdin,
  output: process.stdout
});

function question(prompt) {
  return new Promise((resolve) => {
    rl.question(prompt, resolve);
  });
}

// Generate secure SESSION_SECRET (64 characters)
function generateSessionSecret() {
  return crypto.randomBytes(32).toString('hex');
}

// Generate secure ENCRYPTION_KEY (64 characters)
function generateEncryptionKey() {
  return crypto.randomBytes(32).toString('hex');
}

async function main() {
  console.log('ğŸ” Bodensee Immobilien - Authentication Configuration Generator');
  console.log('==============================================================\n');

  try {
    // Get admin password from user
    console.log('This script will generate secure authentication configuration for your deployment.\n');

    const adminPassword = await question('Enter admin password (will be hashed securely): ');

    if (!adminPassword || adminPassword.length < 8) {
      console.error('âŒ Password must be at least 8 characters long for security.');
      process.exit(1);
    }

    const devPassword = await question('Enter development password (plaintext, for DEV_ADMIN_PASSWORD): ');

    if (!devPassword || devPassword.length < 4) {
      console.error('âŒ Development password must be at least 4 characters long.');
      process.exit(1);
    }

    console.log('\nğŸ”§ Generating secure configuration...\n');

    // Generate all required values
    const adminPasswordHash = hashPassword(adminPassword);
    const sessionSecret = generateSessionSecret();
    const encryptionKey = generateEncryptionKey();

    console.log('âœ… PRODUCTION Environment Variables (add to your deployment secrets):');
    console.log('================================================================');
    console.log(`ADMIN_PASSWORD_HASH=${adminPasswordHash}`);
    console.log(`SESSION_SECRET=${sessionSecret}`);
    console.log(`ENCRYPTION_KEY=${encryptionKey}`);
    console.log(`NODE_ENV=production`);
    console.log('');

    console.log('âœ… DEVELOPMENT Environment Variables (for local/.env file):');
    console.log('===========================================================');
    console.log(`DEV_ADMIN_PASSWORD=${devPassword}`);
    console.log(`SESSION_SECRET=${sessionSecret}`);
    console.log(`ENCRYPTION_KEY=${encryptionKey}`);
    console.log(`NODE_ENV=development`);
    console.log('');

    console.log('ğŸ“ SETUP INSTRUCTIONS:');
    console.log('======================');
    console.log('1. PRODUCTION DEPLOYMENT:');
    console.log('   - Copy the PRODUCTION environment variables above');
    console.log('   - Add them to your deployment platform\'s secrets/environment variables');
    console.log('   - Make sure NODE_ENV=production is set');
    console.log('');
