#!/usr/bin/env node
/**
 * Quick utility to generate secure authentication configuration for deployment
 * Generates ADMIN_PASSWORD_HASH, SESSION_SECRET, and ENCRYPTION_KEY
 */

import crypto from 'crypto';

// Copy the hashPassword function directly to avoid import issues
function hashPassword(password) {
  const salt = crypto.randomBytes(32).toString('hex');
  const hash = crypto.pbkdf2Sync(password, salt, 100000, 64, 'sha512').toString('hex');
  return `${salt}:${hash}`;
}

// Generate secure SESSION_SECRET (64 characters)
function generateSessionSecret() {
  return crypto.randomBytes(32).toString('hex');
}

// Generate secure ENCRYPTION_KEY (64 characters)  
function generateEncryptionKey() {
  return crypto.randomBytes(32).toString('hex');
}

// Generate a secure random password
function generateSecurePassword(length = 16) {
  const charset = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%^&*';
  let password = '';
  for (let i = 0; i < length; i++) {
    password += charset.charAt(Math.floor(Math.random() * charset.length));
  }
  return password;
}

function main() {
  console.log('ğŸ” Generating Secure Authentication Configuration');
  console.log('===============================================\n');

  try {
    // Generate secure passwords
    const adminPassword = generateSecurePassword(16);
    const devPassword = generateSecurePassword(12);

    console.log('ğŸ”§ Generating secure configuration...\n');

    // Generate all required values
    const adminPasswordHash = hashPassword(adminPassword);
    const sessionSecret = generateSessionSecret();
    const encryptionKey = generateEncryptionKey();

    console.log('âœ… PRODUCTION Environment Variables (add to your deployment secrets):');
    console.log('================================================================');
    console.log(`ADMIN_PASSWORD_HASH=${adminPasswordHash}`);
    console.log(`SESSION_SECRET=${sessionSecret}`);
    console.log(`ENCRYPTION_KEY=${encryptionKey}`);
    console.log(`NODE_ENV=production`);
    console.log('');

    console.log('âœ… DEVELOPMENT Environment Variables (for local/.env file):');
    console.log('===========================================================');
    console.log(`DEV_ADMIN_PASSWORD=${devPassword}`);
    console.log(`SESSION_SECRET=${sessionSecret}`);
    console.log(`ENCRYPTION_KEY=${encryptionKey}`);
    console.log(`NODE_ENV=development`);
    console.log('');

    console.log('ğŸ”‘ ADMIN LOGIN CREDENTIALS:');
    console.log('===========================');
    console.log('Username: admin');
    console.log(`Production Password: ${adminPassword}`);
    console.log(`Development Password: ${devPassword}`);
    console.log('');
    console.log('âš ï¸  IMPORTANT: Save these passwords securely! They are auto-generated and shown only once.');
    console.log('');

    console.log('ğŸ“ DEPLOYMENT SETUP INSTRUCTIONS:');
    console.log('==================================');
    console.log('1. Copy the PRODUCTION environment variables above');
    console.log('2. Add them to your Replit deployment secrets:');
    console.log('   - Go to your Replit project');
    console.log('   - Click on "Secrets" in the sidebar');
    console.log('   - Add each environment variable as a secret');
    console.log('3. Make sure NODE_ENV=production is set');
