#!/usr/bin/env node

/**
 * POST-BUILD SCRIPT - PRODUCTION DEPLOYMENT FIX
 * Compiles TypeScript server and creates proper entry point WITHOUT tsx dependency
 * Fixes critical deployment issues for production
 */

import { existsSync, mkdirSync, writeFileSync, renameSync } from 'fs';
import { join, dirname } from 'path';
import { fileURLToPath } from 'url';
import { execSync } from 'child_process';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);
const rootDir = join(__dirname, '..');

console.log('ğŸ”§ Post-Build: Creating production deployment WITHOUT tsx dependency...');

// Ensure dist/server directory exists
const distServerDir = join(rootDir, 'dist', 'server');
if (!existsSync(distServerDir)) {
  mkdirSync(distServerDir, { recursive: true });
  console.log('âœ… Created: dist/server/');
}

// CRITICAL: Build server with production TypeScript config that emits files
console.log('ğŸ“¦ Building Server with production TypeScript config...');
try {
  execSync('npx tsc -p tsconfig.prod.json', { 
    stdio: 'inherit',
    cwd: rootDir,
    timeout: 30000
  });
  console.log('âœ… Server TypeScript compilation completed!');
} catch (error) {
  console.error('âŒ Server build failed:', error.message);
  throw new Error('Server compilation is required for production deployment');
}

// Check if compiled server exists
const compiledServerPath = join(distServerDir, 'index.js');
const renamedServerPath = join(distServerDir, 'server.js');

if (existsSync(compiledServerPath)) {
  // STEP 1: Rename compiled server from index.js to server.js
  renameSync(compiledServerPath, renamedServerPath);
  console.log('âœ… Renamed compiled server: dist/server/index.js â†’ dist/server/server.js');

  // STEP 2: Create new entry point that imports the compiled server
  const entryPointContent = `#!/usr/bin/env node

/**
 * PRODUCTION DEPLOYMENT ENTRY POINT
 * This file is called by "npm start" in production
 * NO tsx dependency - uses compiled JavaScript
 */

console.log('ğŸš€ Production Server Starting (compiled JavaScript)...');
console.log('Port:', process.env.PORT || 5000);
console.log('Environment:', process.env.NODE_ENV || 'production');

// Import and start the compiled server directly
await import('./server.js');
`;

  writeFileSync(compiledServerPath, entryPointContent);
  console.log('âœ… Created production entry point: dist/server/index.js');
  console.log('   â†’ Imports compiled server without tsx dependency');

} else {
  console.error('âŒ Compiled server not found at dist/server/index.js');
  throw new Error('TypeScript compilation failed - no compiled server found');
}

// Verify client build exists
const clientIndexPath = join(rootDir, 'dist', 'public', 'index.html');
if (existsSync(clientIndexPath)) {
  console.log('âœ… Client build found: dist/public/index.html');
} else {
  console.log('âš ï¸  Client build missing - run "npm run build:client" first');
}

console.log('');
console.log('âœ… PRODUCTION DEPLOYMENT READY!');
console.log('   â†’ Server compiled to JavaScript (no tsx dependency)');
console.log('   â†’ Entry point: dist/server/index.js');
console.log('   â†’ Compiled server: dist/server/server.js');
console.log('   â†’ Can run with: node dist/server/index.js');

