#!/usr/bin/env node

/**
 * PRODUCTION BUILD SCRIPT - Fixed Deployment Issues
 * 
 * This script fixes the deployment issues by bypassing the problematic package.json scripts:
 * 1. Uses vite build directly instead of "cd client && npm run build"
 * 2. Uses tsc -p tsconfig.prod.json to compile server to dist/server/index.js
 * 3. Creates proper directory structure for deployment
 * 
 * USAGE:
 *   node scripts/production-build-fixed.js
 * 
 * FOR REPLIT DEPLOYMENT:
 *   Build command: node scripts/production-build-fixed.js
 *   Run command: npm start (or: node dist/server/index.js)
 */

import { execSync } from 'child_process';
import fs from 'fs';
import path from 'path';

console.log('ğŸš€ Production Build - Fixed Deployment Script');
console.log('===============================================');

try {
  // Clean existing build directories
  console.log('ğŸ§¹ Cleaning build directories...');
  if (fs.existsSync('dist')) {
    execSync('rm -rf dist', { stdio: 'inherit' });
  }
  console.log('âœ… Build directories cleaned');

  // Create output directories
  fs.mkdirSync('dist', { recursive: true });
  fs.mkdirSync('dist/server', { recursive: true });

  // BUILD CLIENT - Use vite build directly to avoid package.json issues
  console.log('ğŸ“¦ Building client with vite build (bypassing package.json issues)...');
  try {
    // Try building from client directory first (preserves Tailwind config)
    process.chdir('client');
    execSync('npx vite build --outDir ../dist/public', { stdio: 'inherit' });
    process.chdir('..');
    console.log('âœ… Client built successfully from client directory');
  } catch (error) {
    // Fallback: try building from root
    console.warn('âš ï¸  Building from client directory failed, trying from root...');
    process.chdir('..');
    execSync('npx vite build client --outDir dist/public', { stdio: 'inherit' });
    console.log('âœ… Client built successfully from root directory');
  }

  // BUILD SERVER - Use TypeScript compilation with production config
  console.log('ğŸ”§ Building server with TypeScript production config...');
  execSync('npx tsc -p tsconfig.prod.json', { stdio: 'inherit' });
  console.log('âœ… Server compiled successfully to dist/server/');

  // Verify build outputs
  console.log('ğŸ” Verifying build outputs...');
  const checks = {
    'Client HTML': fs.existsSync('dist/public/index.html'),
    'Server JS': fs.existsSync('dist/server/index.js'),
    'Shared types': fs.existsSync('dist/shared')
  };

  let allGood = true;
  for (const [component, exists] of Object.entries(checks)) {
    const status = exists ? 'âœ…' : 'âŒ';
    console.log(`  ${status} ${component}: ${exists ? 'READY' : 'MISSING'}`);
    if (!exists) allGood = false;
  }

  if (allGood) {
    console.log('\nğŸ‰ PRODUCTION BUILD SUCCESSFUL!');
    console.log('âœ… Client built and ready at dist/public/');
    console.log('âœ… Server compiled and ready at dist/server/index.js');
    console.log('\nğŸš€ Deployment ready!');
    console.log('ğŸ’¡ Start with: npm start (or: node dist/server/index.js)');
    console.log('ğŸ’¡ Build command for Replit: node scripts/production-build-fixed.js');
  } else {
    throw new Error('Build verification failed - some components missing');
  }

} catch (error) {
  console.error('âŒ Production build failed:', error.message);
  console.log('\nğŸ’¡ Troubleshooting tips:');
  console.log('  - Check TypeScript errors with: npx tsc -p tsconfig.prod.json --noEmit');
  console.log('  - Check Vite config with: npx vite build --help');
  console.log('  - Use fallback deployment: node scripts/deploy-fix.js');
  process.exit(1);
}

