#!/usr/bin/env node

/**
 * PRODUCTION START SCRIPT - Fixed Runtime Issues
 * 
 * This script provides a reliable way to start the production server with fallbacks:
 * 1. Try compiled JavaScript at dist/server/index.js (preferred)
 * 2. Fallback to tsx runtime with TypeScript source files
 * 
 * USAGE:
 *   node scripts/production-start-fixed.js
 * 
 * FOR REPLIT DEPLOYMENT:
 *   Run command: node scripts/production-start-fixed.js
 */

import fs from 'fs';
import { execSync } from 'child_process';

console.log('ğŸš€ Production Server Start - Fixed Runtime Script');
console.log('================================================');

// Set production environment
process.env.NODE_ENV = 'production';

try {
  // Method 1: Try compiled JavaScript (preferred)
  if (fs.existsSync('dist/server/index.js')) {
    console.log('ğŸ“¦ Starting with compiled JavaScript server (preferred)...');
    execSync('node dist/server/index.js', { 
      stdio: 'inherit',
      env: { ...process.env, NODE_ENV: 'production' }
    });
  } 
  // Method 2: Fallback to tsx runtime
  else if (fs.existsSync('server/index.ts')) {
    console.log('ğŸ“ Compiled server not found, using tsx runtime fallback...');
    console.log('ğŸ’¡ For faster startup, run: node scripts/production-build-fixed.js');
    execSync('npx tsx server/index.ts', { 
      stdio: 'inherit',
      env: { ...process.env, NODE_ENV: 'production' }
    });
  } 
  // Method 3: Error - no server found
  else {
    throw new Error('No server found! Neither dist/server/index.js nor server/index.ts exist');
  }

} catch (error) {
  console.error('âŒ Production server failed to start:', error.message);
  console.log('\nğŸ’¡ Troubleshooting tips:');
  console.log('  - Run build first: node scripts/production-build-fixed.js');
  console.log('  - Check server source: ls -la server/index.ts');
  console.log('  - Check compiled output: ls -la dist/server/index.js');
  console.log('  - Use alternative: node scripts/deploy-fix.js');
  process.exit(1);
}

scripts/restore-backup.js.content:
#!/usr/bin/env node

import fs from 'fs';
import path from 'path';
import { execSync } from 'child_process';

const BACKUP_DIR = 'backups';

if (process.argv.length < 3) {
  console.log('Usage: npm run restore-backup <backup-name>');
  console.log('\nAvailable backups:');

  if (fs.existsSync(BACKUP_DIR)) {
    const backups = fs.readdirSync(BACKUP_DIR)
      .filter(file => file.endsWith('.tar.gz'))
      .sort()
      .reverse();

    backups.forEach(backup => {
      const stats = fs.statSync(path.join(BACKUP_DIR, backup));
      const size = (stats.size / 1024 / 1024).toFixed(2);
      console.log(`  ğŸ“¦ ${backup.replace('.tar.gz', '')} (${size} MB) - ${stats.mtime.toLocaleDateString()}`);
    });
  } else {
    console.log('  No backups found');
  }
  process.exit(1);
}

const backupName = process.argv[2];
const archivePath = path.join(BACKUP_DIR, `${backupName}.tar.gz`);
const metadataPath = path.join(BACKUP_DIR, `${backupName}-metadata.json`);

if (!fs.existsSync(archivePath)) {
  console.error(`âŒ Backup not found: ${archivePath}`);
  process.exit(1);
}

try {
  console.log('ğŸ”„ Restoring backup checkpoint...');

  // Show backup info
  if (fs.existsSync(metadataPath)) {
    const metadata = JSON.parse(fs.readFileSync(metadataPath, 'utf8'));
    console.log(`ğŸ“ Description: ${metadata.description}`);
    console.log(`ğŸ“… Created: ${new Date(metadata.timestamp).toLocaleString()}`);
    console.log(`ğŸ·ï¸  Git Hash: ${metadata.gitHash}`);
  }

  // Create backup of current state
  console.log('ğŸ’¾ Creating safety backup of current state...');
  const safetyBackup = `safety-backup-${Date.now()}`;
  execSync(`npm run backup-create ${safetyBackup}`, { stdio: 'inherit' });

  // Extract archive
  console.log('ğŸ“¦ Extracting backup archive...');
  execSync(`tar -xzf "${archivePath}"`, { stdio: 'inherit' });

  console.log('âœ… Backup restored successfully!');
  console.log('ğŸ”„ Please restart the development server');

} catch (error) {
  console.error('âŒ Restore failed:', error.message);
  process.exit(1);
}

scripts/setup-auth-tables.js.content:
import { Pool } from "@neondatabase/serverless";
import ws from "ws";

// Use WebSockets for Neon connection
const neonConfig = await import("@neondatabase/serverless").then(
  (m) => m.neonConfig,
);
neonConfig.webSocketConstructor = ws;

const pool = new Pool({ connectionString: process.env.DATABASE_URL });

async function setupAuthTables() {
  try {
    console.log("Setting up Replit Auth tables...");

    // Create sessions table if it doesn't exist
    await pool.query(`
      CREATE TABLE IF NOT EXISTS sessions (
        sid varchar PRIMARY KEY,
        sess jsonb NOT NULL,
        expire timestamp NOT NULL
      );
    `);

    // Create index on expire column
    await pool.query(`
      CREATE INDEX IF NOT EXISTS "IDX_session_expire" ON sessions (expire);
    `);

    // Add new columns to users table if they don't exist
    await pool.query(`
      ALTER TABLE users 
      ADD COLUMN IF NOT EXISTS email varchar,
      ADD COLUMN IF NOT EXISTS first_name varchar,
      ADD COLUMN IF NOT EXISTS last_name varchar,
      ADD COLUMN IF NOT EXISTS profile_image_url varchar;
    `);

    // Check for duplicate emails before adding unique constraint
    const duplicates = await pool.query(`
      SELECT email, COUNT(*) 
      FROM users 
      WHERE email IS NOT NULL 
      GROUP BY email 
      HAVING COUNT(*) > 1;
    `);

    if (duplicates.rows.length === 0) {
      // Try to add unique constraint if it doesn't exist
      await pool.query(`
        DO $$
        BEGIN
          IF NOT EXISTS (
            SELECT 1 FROM pg_constraint 
            WHERE conname = 'users_email_unique'
          ) THEN
            ALTER TABLE users ADD CONSTRAINT users_email_unique UNIQUE (email);
          END IF;
        END $$;
      `);
    } else {
      console.log(
        "Warning: Found duplicate emails, skipping unique constraint",
      );
    }

    console.log("âœ… Replit Auth tables setup completed!");
    process.exit(0);
  } catch (error) {
    console.error("Error setting up auth tables:", error);
    process.exit(1);
  }
}

setupAuthTables();

