              inquiryType: row.inquiryType || row['Inquiry Type'] || 'property_interest',
              status: row.status || row.Status || 'new',
              priority: row.priority || row.Priority || 'normal'
            });
          }

          results.valid.push({ data: validatedData, rowIndex: index + 1 });
        } catch (error) {
