  private async importExistingImages() {
    try {
      const uploadsDir = path.join(process.cwd(), 'uploads');
      if (!fs.existsSync(uploadsDir)) {
        console.log('ğŸ“ Creating uploads directory...');
        fs.mkdirSync(uploadsDir, { recursive: true });
        return;
      }

      const files = fs.readdirSync(uploadsDir)
        .filter(file => /\.(jpg|jpeg|png|gif|webp)$/i.test(file));

      console.log(`ğŸ“ Found ${files.length} image files in uploads`);

      let imported = 0;
      for (const file of files) {
        try {
          // Check by filename instead of ID
          const [existing] = await db.select()
            .from(schema.galleryImages)
            .where(eq(schema.galleryImages.filename, file))
            .limit(1);

          if (!existing) {
            const stats = fs.statSync(path.join(uploadsDir, file));
            const category = file.toLowerCase().includes('360') ? '360' : 
                           file.toLowerCase().includes('hero') ? 'hero' : 'general';

            await this.createGalleryImage({
              filename: file,
              originalName: file,
              mimetype: this.getMimeTypeFromExtension(file),
              size: stats.size,
              category: category,
              alt: file.replace(/\.[^/.]+$/, "").replace(/[-_]/g, ' ')
            });
            imported++;
            console.log(`âœ… Imported: ${file}`);
          }
        } catch (error) {
          console.log(`âš ï¸ Skip import ${file}:`, (error as Error).message);
        }
      }
      console.log(`âœ… Image import completed: ${imported} new images`);
    } catch (error) {
      console.log('ğŸ“ Image import error:', (error as Error).message);
    }
  }

  private getMimeTypeFromExtension(filename: string): string {
    const ext = path.extname(filename).toLowerCase();
    const mimeTypes = {
      '.jpg': 'image/jpeg',
      '.jpeg': 'image/jpeg',
      '.png': 'image/png',
      '.gif': 'image/gif',
      '.webp': 'image/webp'
    };
    return (mimeTypes as any)[ext] || 'image/jpeg';
  }

  // Design Settings Management
  async getDesignSettings(): Promise<DesignSettings | null> {
    try {
      await this.ensureInitialized();
      const [result] = await db.select()
        .from(schema.siteContent)
        .where(eq(schema.siteContent.section, "design_settings"))
        .limit(1);

      return result ? result.content as DesignSettings : null;
    } catch (error) {
      console.error('âŒ Failed to get design settings:', (error as Error).message);
      return null;
    }
  }

  async setDesignSettings(settings: DesignSettings): Promise<boolean> {
    try {
      await this.ensureInitialized();

      // Upsert design settings
      const existing = await this.getDesignSettings();

      if (existing) {
        await db.update(schema.siteContent)
          .set({ 
            content: settings
          })
          .where(eq(schema.siteContent.section, "design_settings"));
      } else {
        await db.insert(schema.siteContent)
          .values({
            section: "design_settings",
            content: settings
          });
      }

      console.log('âœ… Design settings updated successfully');
      return true;
    } catch (error) {
      console.error('âŒ Failed to set design settings:', (error as Error).message);
      return false;
    }
  }

  // Calendar methods (stubs for now)
  async getAllCalendarConnections(): Promise<any[]> {
    console.log('ğŸ“… getAllCalendarConnections: Not implemented yet');
    return [];
