// Utility function to safely extract error messages
function getErrorMessage(error: unknown): string {
  if (error instanceof Error) {
    return error.message;
  }
  if (typeof error === 'string') {
    return error;
  }
  return 'Unknown error occurred';
}

export class SystematicErrorHunter {
  private errors: string[] = [];
  private warnings: string[] = [];
  private fixes: string[] = [];

  async runCompleteSystemScan() {
    console.log('ğŸ” Starting comprehensive system scan...');

    // Reset arrays
    this.errors = [];
    this.warnings = [];
    this.fixes = [];

    // Check database connection
    await this.checkDatabase();

    // Check file system
    await this.checkFileSystem();

    // Check memory usage
    await this.checkMemoryUsage();

    // Check API endpoints
    await this.checkAPIEndpoints();

    // Check environment
    await this.checkEnvironment();

    // Check dependencies
    await this.checkDependencies();

    return {
      summary: {
        errors: this.errors.length,
        warnings: this.warnings.length,
        fixes: this.fixes.length,
        status: this.errors.length === 0 ? 'healthy' : 'needs_attention'
      },
      errors: this.errors,
      warnings: this.warnings,
      fixes: this.fixes,
      recommendations: this.generateRecommendations()
    };
  }

  private async checkDatabase() {
    try {
      const { storage } = await import('./storage');
      // Check if database method exists before calling
      if ('initializeDatabase' in storage) {
        await (storage as any).initializeDatabase();
      }
      this.fixes.push('âœ… Database connection verified');
    } catch (error) {
      this.errors.push(`âŒ Database error: ${getErrorMessage(error)}`);
    }
  }

  private async checkFileSystem() {
    const fs = await import('fs');
    const requiredDirs = ['uploads', 'uploads/properties', 'uploads/360', 'dist', 'dist/public'];

    for (const dir of requiredDirs) {
      try {
        if (!fs.existsSync(dir)) {
          fs.mkdirSync(dir, { recursive: true });
          this.fixes.push(`ğŸ“ Created directory: ${dir}`);
        } else {
          this.fixes.push(`âœ… Directory exists: ${dir}`);
        }
      } catch (error) {
        this.errors.push(`âŒ Directory error for ${dir}: ${getErrorMessage(error)}`);
      }
    }
  }

  private async checkMemoryUsage() {
    try {
      const memUsage = process.memoryUsage();
      const heapUsed = Math.round(memUsage.heapUsed / 1024 / 1024);
      const heapTotal = Math.round(memUsage.heapTotal / 1024 / 1024);

      if (heapUsed > 200) {
        this.warnings.push(`âš ï¸ High memory usage: ${heapUsed}MB`);
      } else {
        this.fixes.push(`âœ… Memory usage normal: ${heapUsed}MB/${heapTotal}MB`);
      }
    } catch (error) {
      this.errors.push(`âŒ Memory check failed: ${getErrorMessage(error)}`);
    }
  }

  private async checkAPIEndpoints() {
    const endpoints = [
      '/api/health',
      '/api/properties',
      '/api/gallery'
    ];

    for (const endpoint of endpoints) {
      try {
        const response = await fetch(`http://localhost:5000${endpoint}`, { 
          signal: AbortSignal.timeout(3000)
        });
        if (response.ok) {
          this.fixes.push(`âœ… API endpoint OK: ${endpoint}`);
        } else {
          this.warnings.push(`âš ï¸ API endpoint issue: ${endpoint} (${response.status})`);
        }
      } catch (error) {
        this.errors.push(`âŒ API endpoint unreachable: ${endpoint}`);
      }
    }
  }

  private async checkEnvironment() {
    try {
      const requiredEnvVars = ['NODE_ENV'];
      const missingVars = requiredEnvVars.filter(v => !process.env[v]);

      if (missingVars.length > 0) {
        this.warnings.push(`âš ï¸ Missing environment variables: ${missingVars.join(', ')}`);
      } else {
        this.fixes.push('âœ… Environment variables configured');
      }

      // Check Node.js version
      const nodeVersion = process.version;
      this.fixes.push(`âœ… Node.js version: ${nodeVersion}`);

    } catch (error) {
      this.errors.push(`âŒ Environment check failed: ${getErrorMessage(error)}`);
    }
  }

  private async checkDependencies() {
    try {
      const fs = await import('fs');
      const path = await import('path');

      // Check if package.json exists
      if (fs.existsSync('package.json')) {
        this.fixes.push('âœ… Package.json found');

        // Check if node_modules exists
        if (fs.existsSync('node_modules')) {
          this.fixes.push('âœ… Dependencies installed');
        } else {
          this.warnings.push('âš ï¸ node_modules missing - run npm install');
        }
      } else {
        this.errors.push('âŒ package.json missing');
      }

    } catch (error) {
      this.errors.push(`âŒ Dependency check failed: ${getErrorMessage(error)}`);
    }
  }

  private generateRecommendations() {
    const recommendations = [];

    if (this.errors.some(e => e.includes('Database'))) {
      recommendations.push('ğŸ”§ Reinitialize database connection');
    }

    if (this.errors.some(e => e.includes('Health endpoint'))) {
      recommendations.push('ğŸ”§ Restart server with proper port binding');
    }

    if (this.errors.some(e => e.includes('Directory'))) {
      recommendations.push('ğŸ”§ Fix file system permissions');
    }

    if (recommendations.length === 0) {
      recommendations.push('âœ… System appears healthy');
    }

    return recommendations;
  }
}

