import { test, expect } from "@playwright/test";
import { faker } from "@faker-js/faker";

/**
 * Comprehensive E2E Test based on AUDIT_BERICHT.md
 * Tests both User and Admin scenarios as documented in the audit report
 * 
 * Use Cases covered:
 * 1. Public User Journey - Immobilienverwaltung from user perspective
 * 2. Admin Workflow - Immobilienverwaltung from admin perspective
 * 3. Error scenarios and security validations
 */

const BASE_URL = process.env.BASE_URL || `http://localhost:${process.env.PORT || 5000}`;
const ADMIN_USER = process.env.ADMIN_USERNAME || "admin";
const ADMIN_PASS = process.env.ADMIN_PASSWORD || "dev-fallback-2025";

// Helper functions
async function fillPropertyForm(page) {
  const fields = [
    { selector: 'input[name*="title"], input[placeholder*="Titel"]', value: `WunderschÃ¶ne Villa am ${faker.location.city()}` },
    { selector: 'input[name*="address"], input[placeholder*="Adresse"]', value: faker.location.streetAddress() },
    { selector: 'input[name*="city"], input[placeholder*="Stadt"]', value: "Ãœberlingen" },
    { selector: 'input[name*="price"], input[placeholder*="Preis"]', value: "850000" },
    { selector: 'input[name*="rooms"], input[placeholder*="Zimmer"]', value: "4" },
    { selector: 'input[name*="area"], input[placeholder*="FlÃ¤che"]', value: "120" },
    { selector: 'textarea, input[name*="description"]', value: "Traumhafte Immobilie mit Bodenseeblick" }
  ];

  for (const field of fields) {
    const element = page.locator(field.selector).first();
    if (await element.count() > 0) {
      await element.fill(field.value);
    }
  }
}

async function fillCustomerForm(page) {
  const fields = [
    { selector: 'input[name*="name"], input[placeholder*="Name"]', value: faker.person.fullName() },
    { selector: 'input[name*="email"], input[placeholder*="Email"]', value: faker.internet.email() },
    { selector: 'input[name*="phone"], input[placeholder*="Telefon"]', value: faker.phone.number() },
    { selector: 'input[name*="budget"], input[placeholder*="Budget"]', value: "500000" }
  ];

  for (const field of fields) {
    const element = page.locator(field.selector).first();
    if (await element.count() > 0) {
      await element.fill(field.value);
    }
  }
}

test.describe("Audit Report E2E Test Suite - User & Admin Scenarios", () => {

  test.describe.configure({ mode: 'serial' });

