    console.log("âœ… Public user journey completed successfully");
  });

  test("Admin Journey: Complete Property Management Workflow (Audit Use Case)", async ({ page }) => {
    console.log("ğŸ” Testing admin workflow based on audit report...");

    // Admin Login (following existing pattern from admin-login.spec.ts)
    await page.goto(`${BASE_URL}/admin/login`, { waitUntil: "domcontentloaded" });

    const usernameField = page.locator(
      'input[name="username"], input[name="email"], input[placeholder*="nutzername"], input[placeholder*="admin"]'
    ).first();
    const passwordField = page.locator('input[type="password"]').first();
