    await Promise.all([
      page.waitForNavigation({ waitUntil: "networkidle", timeout: 10000 }),
      submitButton.click(),
    ]);

    // Verify admin login successful
    const isInAdmin = page.url().includes("/admin") && !page.url().includes("/login");
    expect(isInAdmin).toBeTruthy();
