    // Test Gallery Management
    const galleryTab = page.locator(
      'nav a:has-text("Galerie"), [data-tab="gallery"], button:has-text("Bilder")'
    ).first();

    if (await galleryTab.count() > 0) {
      await galleryTab.click();
      await page.waitForLoadState("networkidle");
      console.log("âœ… Gallery management accessed");
    }

    // Test System Diagnostic (Security check as per audit report)
    const diagnosticTab = page.locator(
      'nav a:has-text("Diagnose"), [data-tab="diagnostic"], button:has-text("System")'
    ).first();

    if (await diagnosticTab.count() > 0) {
