  test("Security and Error Handling Tests (Audit Report Findings)", async ({ page }) => {
    console.log("ğŸ”’ Testing security aspects from audit report...");

    // Test unauthorized admin access
    await page.goto(`${BASE_URL}/admin`, { waitUntil: "domcontentloaded" });

    // Should redirect to login or show access denied
    const isRedirectedToLogin = page.url().includes("/login") || page.url().includes("/auth");
    const hasAccessDenied = await page.locator('text="Access Denied", text="Nicht autorisiert", text="401", text="403"').count() > 0;

    expect(isRedirectedToLogin || hasAccessDenied).toBeTruthy();
    console.log("âœ… Unauthorized access properly handled");

    // Test form validation (XSS prevention as mentioned in audit)
    await page.goto(BASE_URL);
    const contactForm = page.locator('form').first();

    if (await contactForm.count() > 0) {
      // Test XSS attempt
      const xssPayload = '<script>alert("XSS")</script>';
      const nameField = page.locator('input[name*="name"], input[placeholder*="Name"]').first();

      if (await nameField.count() > 0) {
        await nameField.fill(xssPayload);

        // Check if input is properly sanitized
        const fieldValue = await nameField.inputValue();
        expect(fieldValue).not.toContain('<script>');
        console.log("âœ… XSS prevention working");
      }
    }

    // Test SQL injection prevention
    const searchField = page.locator('input[type="search"], input[placeholder*="Suchen"]').first();
    if (await searchField.count() > 0) {
      const sqlPayload = "'; DROP TABLE properties; --";
      await searchField.fill(sqlPayload);
      await searchField.press("Enter");
      await page.waitForTimeout(2000);

      // Page should still be functional (no SQL error)
      expect(await page.locator('body').count()).toBeGreaterThan(0);
      console.log("âœ… SQL injection prevention tested");
    }

    console.log("âœ… Security tests completed");
  });

  test("Performance and Health Check (Audit Report Metrics)", async ({ page }) => {
    console.log("âš¡ Testing performance aspects from audit report...");

    const startTime = Date.now();

    // Test page load performance
    await page.goto(BASE_URL, { waitUntil: "networkidle" });
    const loadTime = Date.now() - startTime;

    // Should load within reasonable time (15 seconds as per health spec)
    expect(loadTime).toBeLessThan(15000);
    console.log(`âœ… Page loaded in ${loadTime}ms`);

    // Test health endpoint
    const response = await page.request.get(`${BASE_URL}/api/health`);
    expect(response.ok()).toBeTruthy();

    const healthData = await response.json();
    expect(healthData).toHaveProperty("ready");
    expect(healthData.ready).toBe(true);
    console.log("âœ… Health endpoint responding correctly");

    // Test image loading performance
    const images = page.locator('img');
    const imageCount = await images.count();

    if (imageCount > 0) {
      // Check if images are loading
      for (let i = 0; i < Math.min(imageCount, 5); i++) {
        const img = images.nth(i);
        const naturalWidth = await img.evaluate((el: HTMLImageElement) => el.naturalWidth);
        expect(naturalWidth).toBeGreaterThan(0);
      }
      console.log("âœ… Images loading correctly");
    }

    console.log("âœ… Performance tests completed");
  });
});

