import { db } from "../db.js";
import * as schema from "@shared/schema";
import { eq } from "drizzle-orm";

export interface SEOData {
  title: string;
  description: string;
  keywords: string;
  ogTitle?: string;
  ogDescription?: string;
  twitterTitle?: string;
  twitterDescription?: string;
}

export class SEOManager {
  private static activeStrategy: any = null;
  private static lastUpdate = 0;
  private static CACHE_DURATION = 5 * 60 * 1000; // 5 minutes

  static async getActiveSEO(section: string): Promise<SEOData | null> {
    try {
      // Cache check
      if (
        this.activeStrategy && 
        Date.now() - this.lastUpdate < this.CACHE_DURATION
      ) {
        return this.getSectionSEO(section);
      }

      // Fetch active strategy
      const activeStrategies = await db
        .select()
        .from(schema.seoStrategiesTable)
        .where(eq(schema.seoStrategiesTable.isActive, true))
        .limit(1);

      if (activeStrategies.length > 0) {
        this.activeStrategy = activeStrategies[0];
        this.lastUpdate = Date.now();
        return this.getSectionSEO(section);
      }

      return null;
    } catch (error) {
      console.error("Error fetching active SEO:", error);
      return null;
    }
  }

  private static getSectionSEO(section: string): SEOData | null {
    if (!this.activeStrategy?.sections) return null;

    try {
      const sections = JSON.parse(this.activeStrategy.sections);
      return sections[section] || null;
    } catch (error) {
      console.error("Error parsing SEO sections:", error);
      return null;
    }
  }

  static async generateMetaTags(section: string, baseUrl: string): Promise<string> {
    const seoData = await this.getActiveSEO(section);

    if (!seoData) {
      return this.getDefaultMetaTags(section, baseUrl);
    }

    return `
    <title>${seoData.title}</title>
    <meta name="description" content="${seoData.description}" />
    <meta name="keywords" content="${seoData.keywords}" />

    <!-- Open Graph -->
    <meta property="og:title" content="${seoData.ogTitle || seoData.title}" />
    <meta property="og:description" content="${seoData.ogDescription || seoData.description}" />
    <meta property="og:url" content="${baseUrl}/${section === 'home' ? '' : section}" />
    <meta property="og:type" content="website" />

    <!-- Twitter -->
    <meta name="twitter:title" content="${seoData.twitterTitle || seoData.title}" />
    <meta name="twitter:description" content="${seoData.twitterDescription || seoData.description}" />
    <meta name="twitter:card" content="summary_large_image" />
    `;
  }

  private static getDefaultMetaTags(section: string, baseUrl: string): string {
    const defaultSEO = {
      home: {
        title: "Bodensee Immobilien MÃ¼ller | Immobilienmakler fÃ¼r die Bodenseeregion",
        description: "Ihr Experte fÃ¼r Immobilien am Bodensee. Ãœber 20 Jahre Erfahrung in der Vermittlung von Wohnungen, HÃ¤usern und Villen in Friedrichshafen und Umgebung.",
        keywords: "Immobilienmakler Bodensee, Wohnung kaufen Bodensee, Haus verkaufen Friedrichshafen"
      },
      properties: {
        title: "Immobilien am Bodensee | Exklusive Angebote",
        description: "Entdecken Sie unsere exklusiven Immobilien in den schÃ¶nsten Lagen rund um den Bodensee.",
        keywords: "Immobilien Bodensee, HÃ¤user Bodensee, Wohnungen Bodensee"
      }
    };

    const seo = defaultSEO[section as keyof typeof defaultSEO] || defaultSEO.home;

    return `
    <title>${seo.title}</title>
    <meta name="description" content="${seo.description}" />
    <meta name="keywords" content="${seo.keywords}" />
    `;
  }

  static clearCache() {
    this.activeStrategy = null;
    this.lastUpdate = 0;
  }
}

