import { Router } from "express";
import { db } from "../db.js";
import * as schema from "@shared/schema";
import { sql } from "drizzle-orm";
import { PerformanceMonitor } from "../lib/performance-monitor.js";
import { SystematicErrorHunter } from "../systematic-error-hunter.js";

const router = Router();

// Basic health check endpoint
router.get("/health", async (req, res) => {
  try {
    const healthData = {
      status: "healthy",
      timestamp: new Date().toISOString(),
      uptime: process.uptime(),
      memory: process.memoryUsage(),
      environment: process.env.NODE_ENV || "development",
      version: "1.0.0",
    };

    res.json(healthData);
  } catch (error) {
    console.error("Health check failed:", error);
    res.status(500).json({
      status: "unhealthy",
      error: error instanceof Error ? error.message : "Unknown error",
      timestamp: new Date().toISOString(),
    });
  }
});

// Enhanced system diagnostics
router.get("/diagnostic", async (req, res) => {
  try {
    const diagnostics = {
      server: {
        status: "running",
        uptime: process.uptime(),
        memory: process.memoryUsage(),
        cpu: process.cpuUsage(),
      },
      database: {
        status: "connected",
      },
      timestamp: new Date().toISOString(),
    };

    res.json(diagnostics);
  } catch (error) {
    console.error("Diagnostic check failed:", error);
    res.status(500).json({
      status: "error",
      error: error instanceof Error ? error.message : "Unknown error",
      timestamp: new Date().toISOString(),
    });
  }
});

// Add comprehensive system check
router.get("/system-check", async (req, res) => {
  try {
    const systemStatus = {
      services: {
        database: await checkDatabase(),
        storage: await checkStorage(),
        external: await checkExternalServices(),
      },
      performance: {
        responseTime: Date.now(),
        memoryUsage: process.memoryUsage(),
        uptime: process.uptime(),
      },
      security: {
        https: req.secure,
        headers: req.headers,
      },
      timestamp: new Date().toISOString(),
    };

    res.json(systemStatus);
  } catch (error) {
    console.error("System check failed:", error);
    res.status(500).json({
      status: "system_error",
      error: error instanceof Error ? error.message : "Unknown error",
      timestamp: new Date().toISOString(),
    });
  }
});

// Systematic error scanning endpoint
router.get("/systematic-scan", async (req, res) => {
  try {
    console.log('ğŸ” Starting systematic scan via API endpoint...');

    const hunter = new SystematicErrorHunter();
    const scanResults = await hunter.runCompleteSystemScan();

    // Transform results to match frontend expected format
    const transformedResults = {
      status: scanResults.summary.status,
      timestamp: new Date().toISOString(),
      summary: {
        total: scanResults.errors.length + scanResults.warnings.length + scanResults.fixes.length,
        critical: scanResults.errors.filter(e => e.includes('âŒ')).length,
        errors: scanResults.errors.length,
        warnings: scanResults.warnings.length,
        info: scanResults.fixes.length
      },
      errors: [
        ...scanResults.errors.map((error, index) => ({
          id: `error_${index}`,
          category: 'error' as const,
          component: 'System',
          message: error,
          timestamp: new Date().toISOString()
        })),
        ...scanResults.warnings.map((warning, index) => ({
          id: `warning_${index}`,
          category: 'warning' as const,
          component: 'System',
          message: warning,
          timestamp: new Date().toISOString()
        })),
        ...scanResults.fixes.map((fix, index) => ({
          id: `info_${index}`,
          category: 'info' as const,
          component: 'System',
          message: fix,
          timestamp: new Date().toISOString()
        }))
      ],
      recommendations: scanResults.recommendations
    };

    console.log('ğŸ“Š Systematic scan completed, returning results');
    res.json(transformedResults);
  } catch (error) {
    console.error("Systematic scan failed:", error);
    res.status(500).json({
      status: "error",
      timestamp: new Date().toISOString(),
      summary: {
        total: 1,
        critical: 1,
        errors: 1,
        warnings: 0,
        info: 0
      },
      errors: [{
        id: 'scan_error',
        category: 'critical',
        component: 'SystematicErrorHunter',
        message: `Systematic scan failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
        timestamp: new Date().toISOString()
      }],
      recommendations: ['ğŸ”§ Check system logs for detailed error information']
    });
  }
});

// Helper functions
async function checkDatabase() {
  try {
    return { status: "connected", responseTime: "< 100ms" };
  } catch (error) {
    return { status: "error", error: error instanceof Error ? error.message : "Unknown error" };
  }
}

async function checkStorage() {
  try {
    return { status: "accessible", space: "available" };
  } catch (error) {
    return { status: "error", error: error instanceof Error ? error.message : "Unknown error" };
  }
}

async function checkExternalServices() {
  try {
    return { 
      openai: { status: "available" },
      notion: { status: "available" },
      email: { status: "available" }
    };
  } catch (error) {
    return { status: "error", error: error instanceof Error ? error.message : "Unknown error" };
  }
}

// Performance monitoring endpoints
router.get("/performance", async (req, res) => {
  try {
    const report = PerformanceMonitor.getReport();
    res.json(report);
  } catch (error) {
