import {
  QueryClient,
  useMutation,
  useQuery,
  useQueryClient,
} from "@tanstack/react-query";
import type { LoginCredentials, AuthResponse } from "@/types/admin";

const TIMEOUT_MS = 8000; // Reduced timeout for faster fallback
const MAX_RETRIES = 2;

// API base configuration
const API_BASE_URL = import.meta.env.VITE_API_URL || "";

interface RequestOptions {
  method?: "GET" | "POST" | "PUT" | "DELETE";
  headers?: Record<string, string>;
  body?: any;
  timeout?: number;
  signal?: AbortSignal;
  retries?: number;
  validateAuth?: boolean;
}

// Custom error types for better error handling
class ApiError extends Error {
  status: number;
  statusText: string;

  constructor(message: string, status: number, statusText: string) {
    super(message);
    this.name = 'ApiError';
    this.status = status;
    this.statusText = statusText;
  }
}

class TimeoutError extends Error {
  status: number;

  constructor(message: string = 'Request timeout') {
    super(message);
    this.name = 'TimeoutError';
    this.status = 408;
  }
}

class NetworkError extends Error {
  status: number;

  constructor(message: string = 'Network error') {
    super(message);
    this.name = 'NetworkError';
    this.status = 0;
  }
}

// Enhanced fetch function with better error handling
const enhancedFetch = async (url: string, options: RequestInit = {}) => {
  const controller = new AbortController();
  const timeoutId = setTimeout(() => {
    console.log(`â° Request timeout (${TIMEOUT_MS}ms) for ${options.method || 'GET'} ${url}`);
    controller.abort();
  }, TIMEOUT_MS);

  try {
    console.log(`ğŸ”„ API Request ${options.method || 'GET'} to: ${url}`);

    const response = await fetch(url, {
      ...options,
      signal: controller.signal,
      headers: {
        'Content-Type': 'application/json',
        ...options.headers,
      },
    });

    clearTimeout(timeoutId);

    if (!response.ok) {
      console.error(`âŒ HTTP ${response.status} for ${url}`);

      // Try to get error message from response body
      let errorMessage = `HTTP ${response.status}: ${response.statusText}`;
      try {
        const errorData = await response.json();
        errorMessage = errorData.message || errorData.error || errorMessage;
      } catch (e) {
        // If JSON parsing fails, use status text
      }

      throw new ApiError(errorMessage, response.status, response.statusText);
    }

    console.log(`âœ… Request successful for ${url}`);
    return response;
  } catch (error: unknown) {
    clearTimeout(timeoutId);

    if (error instanceof Error) {
      if (error.name === 'AbortError' || error.message?.includes('signal is aborted')) {
        console.log(`â° Request timeout for ${options.method || 'GET'} ${url}`);
        throw new TimeoutError('Request timeout');
      }

      if (error.message?.includes('fetch') || error.message?.includes('network')) {
        console.log(`âš ï¸ Network error for ${options.method || 'GET'} ${url}`);
        throw new NetworkError('Network connection failed');
      }

      console.error(`âŒ Request failed for ${options.method || 'GET'} ${url}:`, error.message);
      throw error;
    } else {
      // Handle non-Error objects with better typing
      console.error(`âŒ Request failed for ${options.method || 'GET'} ${url}:`, error);
      const errorMessage = typeof error === 'string' ? error : 'Unknown error occurred';
      throw new NetworkError(errorMessage);
    }
  }
};

// Real API requests with proper error handling
export async function apiRequest<T = any>(
  endpoint: string,
  options: RequestOptions = {}
): Promise<T> {
  const {
    method = 'GET',
    headers = {},
    body,
    timeout = TIMEOUT_MS,
    retries = MAX_RETRIES,
  } = options;

  const url = `${API_BASE_URL}${endpoint}`;

  const requestOptions: RequestInit = {
    method,
    headers: {
      'Content-Type': 'application/json',
      ...headers,
    },
    credentials: 'include',
  };

  if (body && method !== 'GET') {
    requestOptions.body = typeof body === 'string' ? body : JSON.stringify(body);
  }

  let lastError: unknown;

  for (let attempt = 0; attempt <= retries; attempt++) {
    try {
      const response = await enhancedFetch(url, requestOptions);

      // Check if response is JSON
      const contentType = response.headers.get('content-type');
      if (contentType && contentType.includes('application/json')) {
        return await response.json() as T;
      } else {
        return response as unknown as T;
      }
    } catch (error: unknown) {
      lastError = error;
      console.log(`ğŸ”„ Attempt ${attempt + 1}/${retries + 1} failed for ${method} ${endpoint}`);

      if (attempt < retries) {
        await new Promise(resolve => setTimeout(resolve, 1000 * (attempt + 1)));
      }
    }
  }

  const errorMessage = lastError instanceof Error ? lastError.message : String(lastError);
  console.error(`âŒ All attempts failed for ${method} ${endpoint}:`, errorMessage);

  if (lastError instanceof Error) {
    throw lastError;
  } else {
    throw new Error(`Request failed: ${errorMessage}`);
  }
}

