// Placeholder for GalleryImage interface if not defined elsewhere
interface GalleryImage {
  id: string;
  filename: string;
  originalName?: string;
  category?: string;
  metadata?: { type?: string };
  size?: number;
}


export default function ContentEditor() {
  if (process.env.NODE_ENV === 'development') {
    console.log("ğŸš€ ContentEditor component initializing...");
  }

  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [contentData, setContentData] = useState({
    hero: {
      title: "",
      subtitle: "",
      ctaText: "",
      backgroundImage: "",
    },
    about: {
      description: "",
      experience: "",
      sales: "",
    },
    contact: {
      phone: "",
      mobile: "",
      email: "",
      address: "",
      hours: "",
    },
  });
  const [selectedHeroImage, setSelectedHeroImage] = useState<string>("");
  const [activePanel, setActivePanel] = useState<string>("content");
  const [componentError, setComponentError] = useState<string | null>(null);

  // FIXED: Move useThemeConfig() to top level (unconditional hook call)
  // React hooks must be called at the top level, never inside try/catch, loops, or conditions
  const themeConfig = useThemeConfig();
  const designSettings = themeConfig?.designSettings || null;
  const updateSettings = themeConfig?.updateSettings || null;
  const applyTheme = themeConfig?.applyTheme || null;
  const themeLoading = themeConfig?.isLoading || false;

  // FIXED: Handle theme errors in useEffect instead of during render
  useEffect(() => {
    if (!themeConfig || !designSettings) {
      if (process.env.NODE_ENV === 'development') {
        console.warn("âš ï¸ ThemeConfig not available, using fallback");
      }
      setComponentError("Theme configuration not available");
    } else {
      if (process.env.NODE_ENV === 'development') {
        console.log("âœ… ThemeConfig loaded successfully");
      }
      setComponentError(null); // Clear error if theme becomes available
    }
  }, [themeConfig, designSettings]);

  // Galerie-Bilder laden fÃ¼r Hero-Auswahl with improved error handling
  const { data: galleryImages, isLoading: isLoadingGallery, error: galleryError } = useQuery<GalleryImage[]>({
    queryKey: ["/api/gallery"],
    queryFn: async () => {
      if (process.env.NODE_ENV === 'development') {
        console.log("ğŸ“¸ Loading gallery images...");
      }
      try {
        const response = await fetch("/api/gallery", {
          credentials: "include",
          headers: {
            "Cache-Control": "no-cache",
          },
        });

        if (!response.ok) {
          if (process.env.NODE_ENV === 'development') {
            console.warn("âš ï¸ Gallery API not available, using empty array");
          }
          return [];
        }

        const data = await response.json();
        if (process.env.NODE_ENV === 'development') {
          console.log("âœ… Gallery data loaded:", Array.isArray(data) ? data.length : 'invalid', 'items');
        }
        return Array.isArray(data) ? data : [];
      } catch (error) {
        if (process.env.NODE_ENV === 'development') {
          console.warn("âš ï¸ Gallery API error, using empty array:", error);
        }
        return [];
      }
    },
    select: (data) => {
      // Filter nur gÃ¼ltige Bilder und normale Bilder (keine 360Â°)
      const filtered = Array.isArray(data)
        ? data.filter(
            (image) =>
              image &&
              image.id &&
              image.category !== "360" &&
              image.metadata?.type !== "360",
          )
        : [];
      if (process.env.NODE_ENV === 'development') {
        console.log("ğŸ” Filtered gallery images:", filtered.length, 'items');
      }
      return filtered;
    },
    retry: 1,
    staleTime: 5 * 60 * 1000, // 5 minutes
  });

  const { data: siteContent, isLoading, error: siteContentError } = useQuery<ContentSection[]>({
    queryKey: ["/api/site-content"],
    queryFn: async () => {
      if (process.env.NODE_ENV === 'development') {
        console.log("ğŸ“„ Loading site content...");
      }
      try {
        const response = await fetch("/api/site-content", {
          credentials: "include",
          headers: {
            "Cache-Control": "no-cache",
          },
        });

        if (!response.ok) {
          if (process.env.NODE_ENV === 'development') {
            console.warn("âš ï¸ Site content API not available");
          }
          return [];
        }

        const data = await response.json();
        if (process.env.NODE_ENV === 'development') {
