  const retryFailedUploads = () => {
    setBatchUpload(prev => {
      const retryFiles = prev.files.filter(f => f.status === 'failed');
      // Add retry files back to pending queue
      batchControlRef.current.pendingFiles.push(...retryFiles);

      return {
