import { useState, useCallback } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import {
  Camera,
  Upload,
  Trash2,
  Plus,
  Settings,
  Eye,
  Download,
  Share2,
  BarChart,
  CheckCircle,
  AlertTriangle,
  Monitor,
} from "lucide-react";

interface Pro360Image {
  id: string;
  title: string;
  roomType: string;
  area?: number;
  floor?: number;
  file: File;
  preview?: string;
}

interface Property {
  id: string;
  title: string;
  location: string;
}

interface PropertiesResponse {
  properties: Property[];
}

interface Tour {
  id: string;
  title: string;
  description?: string;
  images: TourImage[];
  scenes?: TourImage[]; // Add scenes property for compatibility
}

interface TourImage {
  id: string;
  url: string;
  title: string;
  roomType: string;
}

interface ToursResponse {
  tours: Tour[];
}

export default function ProfessionalTourManager() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedProperty, setSelectedProperty] = useState("");
  const [tourTitle, setTourTitle] = useState("");
  const [tourDescription, setTourDescription] = useState("");
  const [uploadQueue, setUploadQueue] = useState<Pro360Image[]>([]);
  const [processingStatus, setProcessingStatus] = useState<
    "idle" | "processing" | "complete" | "error"
  >("idle");

  const { data: properties } = useQuery<PropertiesResponse>({
    queryKey: ["/api/properties"],
  });

  const { data: existingTours } = useQuery<Tour[]>({
    queryKey: ["/api/tours/professional"],
    enabled: !!selectedProperty,
  });

  // Professional upload mutation with batch processing
  const uploadPro360Mutation = useMutation({
    mutationFn: async (data: {
      images: Pro360Image[];
      propertyId: string;
      tourData: any;
    }) => {
      setProcessingStatus("processing");
      const results = [];

      for (let i = 0; i < data.images.length; i++) {
        const image = data.images[i];
        const formData = new FormData();
        formData.append("image", image.file);
        formData.append("propertyId", data.propertyId);
        formData.append("title", image.title);
        formData.append("roomType", image.roomType);
        formData.append("area", image.area?.toString() || "");
        formData.append("floor", image.floor?.toString() || "1");
        formData.append(
          "description",
          `Professionelle 360Â° Aufnahme: ${image.title}`,
        );

        const response = await fetch("/api/tours/pro/upload", {
          method: "POST",
          body: formData,
          credentials: "include",
        });

        if (!response.ok) {
          throw new Error(`Upload failed for ${image.title}`);
        }

        const result = await response.json();
        results.push(result);

        // Update progress
        toast({
          title: `Verarbeitung ${i + 1}/${data.images.length}`,
          description: `${image.title} erfolgreich verarbeitet`,
        });
      }

      return results;
    },
    onSuccess: (results) => {
      setProcessingStatus("complete");
      queryClient.invalidateQueries({ queryKey: ["/api/properties"] });
      queryClient.invalidateQueries({ queryKey: ["/api/tours/professional"] });
      setUploadQueue([]);
      toast({
        title: "Professionelle Tour erstellt!",
        description: `${results.length} 360Â° Bilder wurden erfolgreich verarbeitet und optimiert.`,
      });
    },
    onError: (error) => {
      setProcessingStatus("error");
      console.error("Professional upload error:", error);
      toast({
        title: "Verarbeitungsfehler",
        description:
          error.message ||
          "Die professionelle Tour konnte nicht erstellt werden",
        variant: "destructive",
      });
    },
  });

  const addToQueue = useCallback(
    (files: FileList | null) => {
      if (!files) return;

      const newImages: Pro360Image[] = Array.from(files)
        .map((file, index) => {
          // Validate file type and size
          if (!file.type.startsWith("image/")) {
            toast({
              title: "UngÃ¼ltiger Dateityp",
              description: `${file.name} ist keine gÃ¼ltige Bilddatei`,
              variant: "destructive",
            });
            return null;
          }

          if (file.size > 50 * 1024 * 1024) {
            // 50MB limit
            toast({
              title: "Datei zu groÃŸ",
              description: `${file.name} ist grÃ¶ÃŸer als 50MB`,
              variant: "destructive",
            });
