          >
            <SelectTrigger>
              <SelectValue placeholder="Typ wÃ¤hlen" />
            </SelectTrigger>
            <SelectContent>
              {PROPERTY_TYPES.map((type) => (
                <SelectItem key={type.value} value={type.value}>
                  {type.label}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
        <div>
          <Label htmlFor="edit-location">Ort</Label>
          <Input
            id="edit-location"
            value={editData.location}
            onChange={(e) =>
              setEditData((prev) => ({ ...prev, location: e.target.value }))
            }
            required
          />
        </div>
        <div>
          <Label htmlFor="edit-price">Preis</Label>
          <Input
            id="edit-price"
            value={editData.price}
            onChange={(e) =>
              setEditData((prev) => ({ ...prev, price: e.target.value }))
            }
            required
          />
        </div>
        <div>
          <Label htmlFor="edit-size">GrÃ¶ÃŸe (mÂ²)</Label>
          <Input
            id="edit-size"
            type="number"
            value={editData.size}
            onChange={(e) =>
              setEditData((prev) => ({ ...prev, size: e.target.value }))
            }
          />
        </div>
        <div>
          <Label htmlFor="edit-rooms">Zimmer</Label>
