            id="edit-rooms"
            type="number"
            value={editData.rooms}
            onChange={(e) =>
              setEditData((prev) => ({ ...prev, rooms: e.target.value }))
            }
            data-testid="edit-input-rooms"
          />
        </div>
        <div>
          <Label htmlFor="edit-bedrooms">Schlafzimmer</Label>
