            onChange={(e) =>
              setEditData((prev) => ({ ...prev, bedrooms: e.target.value }))
            }
            data-testid="edit-input-bedrooms"
          />
        </div>
        <div>
          <Label htmlFor="edit-bathrooms">Badezimmer</Label>
