import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Trash2, Plus, Eye } from "lucide-react";
import VirtualTour from "../landing/virtual-tour";
import { apiRequest } from "@/lib/queryClient";

interface TourEditorProps {
  propertyId: string;
}

interface TourHotspot {
  pitch: number;
  yaw: number;
  text: string;
  sceneId?: string;
  type: "info" | "scene";
}

interface TourScene {
  id: string;
  title: string;
  image: string;
  hotspots?: TourHotspot[];
}

interface TourConfig {
  propertyId: string;
  scenes: TourScene[];
}

export default function TourEditor({ propertyId }: TourEditorProps) {
  const [isEditMode, setIsEditMode] = useState(false);
  const queryClient = useQueryClient();

  // Load tour configuration
  const { data: tourConfig, isLoading } = useQuery({
    queryKey: ["tour-config", propertyId],
    queryFn: () => apiRequest<TourConfig>(`/api/tours/${propertyId}`),
  });

  // Add hotspot mutation
  const addHotspotMutation = useMutation({
    mutationFn: ({
      sceneId,
      hotspot,
    }: {
      sceneId: string;
      hotspot: TourHotspot;
    }) =>
      apiRequest(`/api/tours/${propertyId}/scenes/${sceneId}/hotspots`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(hotspot),
      }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["tour-config", propertyId] });
    },
  });

  // Remove hotspot mutation
  const removeHotspotMutation = useMutation({
    mutationFn: ({
      sceneId,
      hotspotIndex,
    }: {
      sceneId: string;
      hotspotIndex: number;
    }) =>
      apiRequest(
        `/api/tours/${propertyId}/scenes/${sceneId}/hotspots/${hotspotIndex}`,
        {
          method: "DELETE",
        },
      ),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["tour-config", propertyId] });
    },
  });

  const handleAddHotspot = (sceneId: string, hotspot: TourHotspot) => {
    addHotspotMutation.mutate({ sceneId, hotspot });
  };

  const handleRemoveHotspot = (sceneId: string, hotspotIndex: number) => {
    removeHotspotMutation.mutate({ sceneId, hotspotIndex });
  };

  if (isLoading) {
    return (
      <Card>
        <CardContent className="p-6">
          <div className="animate-pulse">Tour wird geladen...</div>
        </CardContent>
      </Card>
    );
  }

  if (!tourConfig?.scenes || tourConfig.scenes.length === 0) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>360Â° Tour Editor</CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-gray-600 mb-4">
            Keine 360Â°-Bilder fÃ¼r diese Immobilie vorhanden. Laden Sie zuerst
            360Â°-Bilder in der Galerie hoch.
          </p>
          <Badge variant="outline">
            Tipp: Bilder werden automatisch als 360Â° erkannt
          </Badge>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center justify-between">
            <span>360Â° Tour Editor</span>
            <div className="flex space-x-2">
              <Button
                variant={isEditMode ? "destructive" : "default"}
                size="sm"
                onClick={() => setIsEditMode(!isEditMode)}
              >
                {isEditMode ? (
                  <>
                    <Eye className="w-4 h-4 mr-2" />
                    Vorschau
                  </>
                ) : (
                  <>
                    <Plus className="w-4 h-4 mr-2" />
                    Bearbeiten
                  </>
                )}
              </Button>
            </div>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="mb-4">
            <h3 className="text-sm font-medium text-gray-600 mb-2">
              Tour Statistiken:
            </h3>
            <div className="flex space-x-4">
              <Badge variant="outline">{tourConfig.scenes.length} RÃ¤ume</Badge>
              <Badge variant="outline">
                {tourConfig.scenes.reduce(
                  (total, scene) => total + (scene.hotspots?.length || 0),
                  0,
                )}{" "}
                Hotspots
              </Badge>
            </div>
          </div>

          {isEditMode && (
            <div className="bg-blue-50 p-4 rounded-lg mb-4">
              <h4 className="font-medium text-blue-900 mb-2">
                ğŸ¯ Hotspot-Editor Anleitung
              </h4>
              <ul className="text-sm text-blue-800 space-y-1">
                <li>â€¢ Klicken Sie auf "+" um Hotspot-Modus zu aktivieren</li>
                <li>
