  const upload360ImageMutation = useMutation({
    mutationFn: async (data: {
      file: File;
      propertyId: string;
      title: string;
    }) => {
      const formData = new FormData();
      formData.append("image", data.file);
      formData.append("propertyId", data.propertyId);
      formData.append("title", data.title);
      formData.append("type", "360");

      const response = await fetch("/api/gallery/upload-360", {
        method: "POST",
        body: formData,
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Upload fehlgeschlagen");
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/properties"] });
      toast({
        title: "360Â° Bild hochgeladen",
        description: "Das 360Â° Bild wurde erfolgreich hochgeladen",
      });
    },
    onError: () => {
      toast({
        title: "Fehler",
        description: "Das 360Â° Bild konnte nicht hochgeladen werden",
        variant: "destructive",
      });
    },
  });

  const handleFileUpload = (files: FileList | null, title: string) => {
