    }

    const file = files[0];
    if (!file.type.startsWith("image/")) {
      toast({
        title: "UngÃ¼ltiger Dateityp",
        description: "Nur Bilddateien sind erlaubt",
        variant: "destructive",
      });
      return;
    }

    upload360ImageMutation.mutate({
      file,
      propertyId: selectedProperty,
      title,
    });
  };

  return (
    <div className="space-y-6">
      <Card>
        <CardContent className="p-6">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold text-gray-900">
              360Â° Touren verwalten
            </h2>
            <Camera className="w-6 h-6 text-[var(--arctic-blue)]" />
          </div>

          {/* Upload Section */}
          <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 mb-6">
            <div className="text-center">
              <Camera className="w-12 h-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">
                360Â° Bilder hochladen
              </h3>
              <p className="text-gray-600 mb-4">
                Laden Sie equirectangular 360Â° Bilder fÃ¼r Ihre Immobilien hoch
