import { useEffect, useRef, useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import {
  Play,
  Pause,
  RotateCcw,
  Maximize,
  Map,
  Info,
  Settings,
  Share2,
  Volume2,
  VolumeX,
  Eye,
  Camera,
  Compass,
  Grid,
  Layers,
  Home,
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";

// Extend Window interface for Pannellum
declare global {
  interface Window {
    pannellum: any;
  }
}

interface TourScene {
  id: string;
  title: string;
  image: string;
  description?: string;
  roomType?: string;
  area?: number;
  hotspots?: Array<{
    pitch: number;
    yaw: number;
    text: string;
    sceneId?: string;
    type?: "info" | "scene" | "media";
  }>;
}

interface VirtualTourProps {
  scenes: TourScene[];
  propertyTitle: string;
}

export default function EnhancedVirtualTour({
  scenes,
  propertyTitle,
}: VirtualTourProps) {
  const viewerRef = useRef<HTMLDivElement>(null);
  const [currentScene, setCurrentScene] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [pannellumViewer, setPannellumViewer] = useState<any>(null);
  const [activeTab, setActiveTab] = useState("tour");
  const [showHotspots, setShowHotspots] = useState(true);
  const [showCompass, setShowCompass] = useState(true);
  const [audioEnabled, setAudioEnabled] = useState(false);
  const [tourProgress, setTourProgress] = useState(0);
  const { toast } = useToast();

  useEffect(() => {
    const loadPannellum = async () => {
      if (typeof window !== "undefined" && !window.pannellum) {
        const link = document.createElement("link");
        link.rel = "stylesheet";
        link.href =
          "https://cdn.jsdelivr.net/npm/pannellum@2.5.6/build/pannellum.css";
        document.head.appendChild(link);

        const script = document.createElement("script");
        script.src =
          "https://cdn.jsdelivr.net/npm/pannellum@2.5.6/build/pannellum.js";
        script.onload = initViewer;
        document.head.appendChild(script);
      } else if (window.pannellum) {
        initViewer();
      }
    };

    const initViewer = () => {
      if (viewerRef.current && scenes.length > 0 && window.pannellum) {
        const createViewer = (url: string) => {
          try {
            const viewer = window.pannellum.viewer(viewerRef.current, {
              type: "equirectangular",
              panorama: url,
              autoLoad: true,
              hotSpots: showHotspots
                ? scenes[currentScene].hotspots?.map((hotspot) => ({
                    pitch: hotspot.pitch,
                    yaw: hotspot.yaw,
                    type: hotspot.type || "info",
                    text: hotspot.text,
                    URL: hotspot.sceneId ? undefined : hotspot.text,
                    clickHandlerFunc: hotspot.sceneId
                      ? () => {
                          const sceneIndex = scenes.findIndex(
                            (s) => s.id === hotspot.sceneId,
                          );
                          if (sceneIndex !== -1) {
                            handleSceneChange(sceneIndex);
                          }
                        }
                      : undefined,
                  })) || []
                : [],
              compass: showCompass,
              northOffset: 0,
              showZoomCtrl: true,
              keyboardZoom: true,
              mouseZoom: true,
              showFullscreenCtrl: false,
              autoRotate: isPlaying ? -2 : 0,
            });

            // Warten bis der Viewer vollstÃ¤ndig geladen ist
            viewer.on("load", () => {
              setPannellumViewer(viewer);
            });
          } catch (error) {
            console.error("Error creating 360Â° viewer:", error);
          }
        };

        // Validate image URL
        const imageUrl = scenes[currentScene].image;
        console.log("Loading 360Â° image:", imageUrl);

        // Check if image exists before loading
        const img = new Image();
        img.onload = () => {
          console.log("360Â° image loaded successfully:", imageUrl);
          createViewer(imageUrl);
        };
        img.onerror = () => {
          console.error("Failed to load 360Â° image:", imageUrl);
          // Try fallback URL or show error
          if (imageUrl.startsWith("/uploads/")) {
            const fallbackUrl = `/api/gallery${imageUrl.replace("/uploads/", "/")}`;
            console.log("Trying fallback URL:", fallbackUrl);

            // Test fallback URL
            const fallbackImg = new Image();
            fallbackImg.onload = () => createViewer(fallbackUrl);
            fallbackImg.onerror = () => {
              console.error("Fallback URL also failed:", fallbackUrl);
              // Show error state in viewer
              if (viewerRef.current) {
                viewerRef.current.innerHTML = `
                    <div class="flex items-center justify-center h-full bg-gray-100 text-gray-500">
                      <div class="text-center">
                        <p class="text-lg mb-2">360Â° Bild nicht verfÃ¼gbar</p>
                        <p class="text-sm">Das Bild konnte nicht geladen werden.</p>
                      </div>
                    </div>
                  `;
              }
            };
            fallbackImg.src = fallbackUrl;
          } else {
            // Show error state
            if (viewerRef.current) {
              viewerRef.current.innerHTML = `
                  <div class="flex items-center justify-center h-full bg-gray-100 text-gray-500">
