                      </Button>
                    ))}
                  </div>
                </div>
              </div>
            )}
          </div>
        </TabsContent>

        <TabsContent value="navigation" className="p-4">
          <div className="space-y-4">
            <h3 className="text-lg font-semibold">Szenen-Ãœbersicht</h3>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
              {scenes.map((scene, index) => (
                <Card
                  key={scene.id}
                  className={`cursor-pointer transition-all hover:shadow-md ${
                    index === currentScene
                      ? "ring-2 ring-[var(--arctic-blue)]"
                      : ""
                  }`}
                  onClick={() => {
                    handleSceneChange(index);
                    setActiveTab("tour");
                  }}
                >
                  <CardContent className="p-3">
                    <div className="aspect-video bg-gray-200 rounded mb-2 relative overflow-hidden">
                      <img
                        src={scene.image}
                        alt={scene.title}
