              </div>

              <hr />

              <div className="space-y-2">
                <h4 className="font-medium">Tour-Aktionen</h4>
                <div className="flex flex-wrap gap-2">
                  <Button variant="outline" size="sm" onClick={resetView}>
                    <RotateCcw className="w-4 h-4 mr-1" />
                    Ansicht zurÃ¼cksetzen
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={toggleFullscreen}
                  >
                    <Maximize className="w-4 h-4 mr-1" />
                    Vollbild
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={shareVirtualTour}
                  >
                    <Share2 className="w-4 h-4 mr-1" />
                    Tour teilen
                  </Button>
                </div>
              </div>
            </div>
          </div>
        </TabsContent>
      </Tabs>
    </div>
  );
}

// Main VirtualTour component that uses real uploaded images
export const VirtualTour = ({
  property,
  tour360Images = [],
}: {
  property: any;
  tour360Images?: any[];
}) => {
  // Use passed tour360Images or fallback to property features
  const tourImages =
    tour360Images.length > 0
      ? tour360Images
      : property.features?.tour360Images || [];

  console.log("Property features:", property.features);
  console.log("Tour 360 images:", tourImages);

  const scenes: TourScene[] =
    tourImages.length > 0
      ? tourImages.map((imageData: any, index: number) => {
          const imageId =
            typeof imageData === "string" ? imageData : imageData.id;
          const title =
            typeof imageData === "object" && imageData.metadata?.title
              ? imageData.metadata.title
              : index === 0
                ? "Hauptraum"
                : index === 1
                  ? "Schlafzimmer"
                  : index === 2
                    ? "KÃ¼che"
                    : `Raum ${index + 1}`;

          return {
            id: `scene-${index}`,
            title,
            image: `/api/gallery/${imageId}/image`,
            description: `360Â° Rundblick: ${title}`,
            roomType: title,
            area: index === 0 ? 45 : index === 1 ? 25 : index === 2 ? 20 : 30,
            hotspots:
              index < tourImages.length - 1
                ? [
                    {
                      pitch: -10,
                      yaw: 90,
                      text: `Zur nÃ¤chsten Szene`,
                      sceneId: `scene-${index + 1}`,
                      type: "scene" as const,
                    },
                  ]
                : tourImages.length > 1
                  ? [
                      {
                        pitch: -10,
                        yaw: 270,
                        text: "ZurÃ¼ck zum Anfang",
                        sceneId: "scene-0",
                        type: "scene" as const,
                      },
                    ]
                  : [],
          };
        })
      : [];

  return (
    <RealVirtualTour
      scenes={scenes}
      propertyTitle={property.title || "360Â° Tour"}
    />
  );
};

