type CustomerFormData = z.infer<typeof customerSchema>;

interface Customer {
  id: string;
  name: string;
  email: string;
  phone?: string;
  type: string;
  status: string;
  leadScore: number;
  assignedAgent?: string;
  budgetMin?: number;
  budgetMax?: number;
  timeline?: string;
  address?: string;
  occupation?: string;
  company?: string;
  notes?: string;
