export default function NewsletterManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [newsletter, setNewsletter] = useState({
    subject: "",
    content: "",
    category: "",
  });

  const { data: newsletters } = useQuery({
    queryKey: ["/api/newsletters"],
  });

  const { data: subscribers } = useQuery({
    queryKey: ["/api/newsletter-subscribers"],
  });

  const createNewsletterMutation = useMutation({
    mutationFn: async (data: any) => {
      await apiRequest("/api/newsletters", { method: "POST", body: data });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/newsletters"] });
      setNewsletter({ subject: "", content: "", category: "" });
      toast({
        title: "Newsletter erstellt",
        description: "Der Newsletter wurde erfolgreich erstellt",
      });
    },
    onError: () => {
      toast({
        title: "Fehler",
        description: "Der Newsletter konnte nicht erstellt werden",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (status: string) => {
    if (!newsletter.subject || !newsletter.content) {
      toast({
        title: "Fehler",
        description: "Bitte f√ºllen Sie alle Felder aus",
        variant: "destructive",
      });
      return;
    }

    createNewsletterMutation.mutate({
      ...newsletter,
      status,
    });
  };

  const subscriberStats = {
    total: Array.isArray(subscribers) ? subscribers.length : 0,
    active: Array.isArray(subscribers)
      ? subscribers.filter((s: any) => s.status === "active").length
      : 0,
    newThisWeek: Array.isArray(subscribers)
      ? subscribers.filter((s: any) => {
          const weekAgo = new Date();
          weekAgo.setDate(weekAgo.getDate() - 7);
          return new Date(s.subscribeDate) > weekAgo;
        }).length
      : 0,
    openRate: "78.5%", // This would come from analytics
  };

  const recentCampaigns = Array.isArray(newsletters)
    ? newsletters.slice(0, 3)
    : [];

  return (
    <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
      {/* Newsletter Editor */}
      <div className="lg:col-span-2">
        <Card className="border border-gray-200">
          <CardContent className="p-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">
              Newsletter erstellen
            </h2>

            <form className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="subject">Betreff</Label>
                  <Input
                    id="subject"
                    placeholder="Newsletter Betreff"
                    value={newsletter.subject}
                    onChange={(e) =>
                      setNewsletter((prev) => ({
                        ...prev,
                        subject: e.target.value,
                      }))
                    }
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="category">Kategorie</Label>
                  <Select
                    value={newsletter.category}
                    onValueChange={(value) =>
                      setNewsletter((prev) => ({ ...prev, category: value }))
                    }
                  >
                    <SelectTrigger>
                      <SelectValue placeholder="Kategorie w√§hlen" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="new_properties">
                        Neue Immobilien
                      </SelectItem>
                      <SelectItem value="market_update">Marktupdate</SelectItem>
                      <SelectItem value="tips">Tipps & Ratgeber</SelectItem>
                      <SelectItem value="company_news">
                        Unternehmensnews
                      </SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="content">Newsletter Inhalt</Label>
                <Textarea
                  id="content"
                  rows={12}
                  placeholder="Ihren Newsletter-Inhalt hier eingeben..."
                  value={newsletter.content}
                  onChange={(e) =>
                    setNewsletter((prev) => ({
                      ...prev,
                      content: e.target.value,
                    }))
                  }
                  className="resize-none"
                />
              </div>

              <div className="flex items-center space-x-4">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => handleSubmit("draft")}
                  disabled={createNewsletterMutation.isPending}
                >
                  <Save className="w-4 h-4 mr-2" />
                  Als Entwurf speichern
                </Button>
                <Button
                  type="button"
                  variant="outline"
                  className="border-[var(--arctic-blue)] text-[var(--arctic-blue)] hover:bg-[var(--arctic-blue)]/5"
                >
                  <Eye className="w-4 h-4 mr-2" />
                  Vorschau
                </Button>
                <Button
                  type="button"
                  onClick={() => handleSubmit("sent")}
                  disabled={createNewsletterMutation.isPending}
                  className="bg-green-500 hover:bg-green-600"
                >
                  <Send className="w-4 h-4 mr-2" />
                  Newsletter versenden
                </Button>
              </div>
            </form>
          </CardContent>
        </Card>
      </div>

      {/* Sidebar */}
      <div className="space-y-6">
        {/* Subscriber Stats */}
        <Card className="border border-gray-200">
          <CardContent className="p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">
              Abonnenten Statistiken
            </h3>

            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <span className="text-gray-600">Gesamt Abonnenten</span>
                <span className="font-semibold text-gray-900">
                  {subscriberStats.total}
                </span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-gray-600">Aktive Abonnenten</span>
                <span className="font-semibold text-green-600">
                  {subscriberStats.active}
                </span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-gray-600">Neue diese Woche</span>
                <span className="font-semibold text-blue-600">
                  {subscriberStats.newThisWeek}
                </span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-gray-600">√ñffnungsrate</span>
                <span className="font-semibold text-gray-900">
                  {subscriberStats.openRate}
                </span>
              </div>
            </div>

            <Button className="w-full mt-4 bg-[var(--ruskin-blue)] hover:bg-[var(--ruskin-blue)]/90">
              <Download className="w-4 h-4 mr-2" />
              CSV Export
            </Button>
          </CardContent>
        </Card>

        {/* Recent Campaigns */}
        <Card className="border border-gray-200">
          <CardContent className="p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">
              Letzte Kampagnen
            </h3>

            <div className="space-y-4">
              {recentCampaigns.map((campaign: any, index: number) => (
                <div
                  key={campaign.id}
                  className={`border-l-4 pl-4 ${
                    index === 0
                      ? "border-green-500"
                      : index === 1
                        ? "border-blue-500"
                        : "border-orange-500"
                  }`}
                >
                  <h4 className="font-medium text-gray-900">
                    {campaign.subject}
                  </h4>
                  <p className="text-sm text-gray-600">
                    Gesendet:{" "}
                    {campaign.sentAt
                      ? new Date(campaign.sentAt).toLocaleDateString("de-DE")
                      : "Entwurf"}
                  </p>
                  {campaign.openRate && (
                    <p
                      className={`text-sm ${
                        index === 0
                          ? "text-green-600"
                          : index === 1
                            ? "text-blue-600"
                            : "text-orange-600"
                      }`}
                    >
                      √ñffnungsrate: {campaign.openRate}%
                    </p>
                  )}
                </div>
              ))}

              {recentCampaigns.length === 0 && (
                <p className="text-gray-500 text-center py-4">
                  Keine Kampagnen vorhanden
                </p>
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

import { useState, useEffect } from 'react';
import { Bell, X } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from '@/components/ui/popover';
import { Card, CardContent } from '@/components/ui/card';

interface Notification {
  id: string;
  title: string;
  message: string;
  timestamp: Date;
  read: boolean;
  type: 'info' | 'warning' | 'success' | 'error';
}

export default function NotificationBell() {
  const [notifications, setNotifications] = useState<Notification[]>([
    {
      id: '1',
      title: 'Neue Anfrage',
      message: 'Sie haben eine neue Immobilien-Anfrage erhalten',
      timestamp: new Date(Date.now() - 5 * 60 * 1000),
      read: false,
      type: 'info'
    },
    {
      id: '2',
      title: 'AI Bewertung',
      message: 'Eine neue AI Bewertung wurde angefordert',
      timestamp: new Date(Date.now() - 15 * 60 * 1000),
      read: false,
      type: 'success'
    }
  ]);

  const unreadCount = notifications.filter(n => !n.read).length;

  const markAsRead = (id: string) => {
    setNotifications(prev => 
      prev.map(n => n.id === id ? { ...n, read: true } : n)
    );
  };

  const markAllAsRead = () => {
    setNotifications(prev => prev.map(n => ({ ...n, read: true })));
  };

  const deleteNotification = (id: string) => {
    setNotifications(prev => prev.filter(n => n.id !== id));
  };

  return (
    <Popover>
      <PopoverTrigger asChild>
        <Button variant="ghost" size="icon" className="relative">
          <Bell className="h-5 w-5" />
          {unreadCount > 0 && (
            <Badge 
              variant="destructive" 
              className="absolute -top-1 -right-1 h-5 w-5 flex items-center justify-center p-0 text-xs"
            >
              {unreadCount}
            </Badge>
          )}
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-80 p-0" align="end">
        <div className="flex items-center justify-between p-4 border-b">
          <h3 className="font-semibold">Benachrichtigungen</h3>
          {unreadCount > 0 && (
            <Button variant="ghost" size="sm" onClick={markAllAsRead}>
              Alle als gelesen markieren
            </Button>
          )}
        </div>
        <div className="max-h-80 overflow-y-auto">
          {notifications.length === 0 ? (
            <div className="p-4 text-center text-muted-foreground">
              Keine Benachrichtigungen
            </div>
          ) : (
            notifications.map((notification) => (
              <Card 
                key={notification.id} 
                className={`m-2 cursor-pointer transition-colors ${
                  !notification.read ? 'bg-blue-50 dark:bg-blue-950/20' : ''
                }`}
                onClick={() => markAsRead(notification.id)}
              >
                <CardContent className="p-3">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="font-medium text-sm">{notification.title}</div>
                      <div className="text-xs text-muted-foreground mt-1">
                        {notification.message}
                      </div>
                      <div className="text-xs text-muted-foreground mt-2">
                        {notification.timestamp.toLocaleTimeString('de-DE')}
                      </div>
                    </div>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={(e) => {
                        e.stopPropagation();
                        deleteNotification(notification.id);
                      }}
                    >
                      <X className="h-3 w-3" />
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))
          )}
        </div>
      </PopoverContent>
    </Popover>
  );
}

import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Switch } from "@/components/ui/switch";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import {
  ExternalLink,
  CheckCircle,
  AlertCircle,
  Clock,
  RefreshCw,
  Settings,
  Users,
  Building,
  Mail,
  Calendar,
} from "lucide-react";

interface ConnectionStatus {
  success: boolean;
  message?: string;
  error?: string;
}

interface NotionTask {
  id: string;
  title: string;
  status: string;
  priority: string;
  description?: string;
  dueDate?: string;
  assignee?: string;
}

export default function NotionIntegration() {
  const [autoSyncEnabled, setAutoSyncEnabled] = useState(true);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Test Notion connection
  const { data: connectionStatus, isLoading: connectionLoading } = useQuery<ConnectionStatus>({
    queryKey: ["/api/notion/test"],
    refetchInterval: 30000, // Check connection every 30 seconds
  });

  // Get Notion tasks
  const { data: notionTasks, isLoading: tasksLoading } = useQuery<NotionTask[]>({
    queryKey: ["/api/notion/tasks"],
    enabled: connectionStatus?.success === true,
  });

  const syncInquiryMutation = useMutation({
    mutationFn: async (inquiryId: string) => {
      return await apiRequest(`/api/notion/sync-inquiry/${inquiryId}`, { method: "POST" });
    },
    onSuccess: () => {
      toast({
        title: "Erfolgreich synchronisiert",
        description: "Anfrage wurde zu Notion hinzugef√ºgt",
      });
    },
    onError: () => {
      toast({
        title: "Synchronisation fehlgeschlagen",
        description: "Anfrage konnte nicht zu Notion hinzugef√ºgt werden",
        variant: "destructive",
      });
    },
  });

  const syncPropertyMutation = useMutation({
    mutationFn: async (propertyId: string) => {
      return await apiRequest(`/api/notion/sync-property/${propertyId}`, { method: "POST" });
    },
    onSuccess: () => {
      toast({
        title: "Erfolgreich synchronisiert",
        description: "Immobilie wurde zu Notion hinzugef√ºgt",
      });
    },
    onError: () => {
      toast({
        title: "Synchronisation fehlgeschlagen",
        description: "Immobilie konnte nicht zu Notion hinzugef√ºgt werden",
        variant: "destructive",
      });
    },
  });

  const createTaskMutation = useMutation({
    mutationFn: async (taskData: any) => {
      return await apiRequest("/api/notion/create-task", { method: "POST", body: taskData });
    },
    onSuccess: () => {
      toast({
        title: "Aufgabe erstellt",
        description: "Neue Aufgabe wurde in Notion erstellt",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/notion/tasks"] });
    },
    onError: () => {
      toast({
        title: "Fehler",
        description: "Aufgabe konnte nicht erstellt werden",
        variant: "destructive",
      });
    },
  });

  const getPriorityColor = (priority: string) => {
    switch (priority?.toLowerCase()) {
      case "hoch":
        return "bg-red-100 text-red-800";
      case "mittel":
        return "bg-yellow-100 text-yellow-800";
      case "niedrig":
        return "bg-green-100 text-green-800";
      default:
        return "bg-gray-100 text-gray-800";
    }
  };

  const getStatusColor = (status: string) => {
    switch (status?.toLowerCase()) {
      case "zu erledigen":
        return "bg-blue-100 text-blue-800";
      case "in bearbeitung":
        return "bg-orange-100 text-orange-800";
      case "erledigt":
        return "bg-green-100 text-green-800";
      default:
        return "bg-gray-100 text-gray-800";
    }
  };

  return (
    <div className="space-y-6">
      {/* Connection Status */}
      <Card className="border border-gray-200">
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <ExternalLink className="w-5 h-5" />
            <span>Notion Integration</span>
            {connectionStatus?.success ? (
              <CheckCircle className="w-5 h-5 text-green-500" />
            ) : (
              <AlertCircle className="w-5 h-5 text-red-500" />
            )}
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <Label htmlFor="auto-sync">Automatische Synchronisation</Label>
                <Switch
                  id="auto-sync"
                  checked={autoSyncEnabled}
                  onCheckedChange={setAutoSyncEnabled}
                />
              </div>
              <div className="flex items-center space-x-2 text-sm">
                <div
                  className={`w-3 h-3 rounded-full ${connectionStatus?.success ? "bg-green-500" : "bg-red-500"}`}
                ></div>
                <span
                  className={
                    connectionStatus?.success
                      ? "text-green-700"
                      : "text-red-700"
                  }
                >
                  {connectionStatus?.success ? "Verbunden" : "Nicht verbunden"}
                </span>
              </div>
            </div>

            <div className="space-y-2">
              <h4 className="font-semibold flex items-center space-x-2">
                <Building className="w-4 h-4" />
                <span>Immobilien</span>
              </h4>
              <p className="text-sm text-gray-600">
                Automatisch zu Notion synchronisiert
              </p>
            </div>

            <div className="space-y-2">
              <h4 className="font-semibold flex items-center space-x-2">
                <Mail className="w-4 h-4" />
                <span>Kundenanfragen</span>
              </h4>
              <p className="text-sm text-gray-600">
                Automatisch zu Notion synchronisiert
              </p>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Notion Tasks */}
      <Card className="border border-gray-200">
        <CardHeader>
          <CardTitle className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <Calendar className="w-5 h-5" />
              <span>Notion Aufgaben</span>
            </div>
            <Button
              variant="outline"
              size="sm"
              onClick={() =>
                queryClient.invalidateQueries({
                  queryKey: ["/api/notion/tasks"],
                })
              }
              disabled={tasksLoading}
            >
              <RefreshCw
                className={`w-4 h-4 mr-2 ${tasksLoading ? "animate-spin" : ""}`}
              />
              Aktualisieren
            </Button>
          </CardTitle>
        </CardHeader>
        <CardContent>
          {tasksLoading ? (
            <div className="space-y-3">
              {[...Array(3)].map((_, i) => (
                <div
                  key={i}
                  className="h-16 bg-gray-100 animate-pulse rounded-lg"
                ></div>
              ))}
            </div>
          ) : (
            <div className="space-y-4">
              {notionTasks && notionTasks.length > 0 ? (
                notionTasks.map((task: NotionTask) => (
                  <div
                    key={task.id}
                    className="border border-gray-200 rounded-lg p-4 hover:bg-gray-50 transition-colors"
                  >
                    <div className="flex items-start justify-between mb-2">
                      <h4 className="font-medium text-gray-900">
                        {task.title}
                      </h4>
                      <div className="flex items-center space-x-2">
                        <Badge className={getPriorityColor(task.priority)}>
                          {task.priority || "Normal"}
                        </Badge>
                        <Badge className={getStatusColor(task.status)}>
                          {task.status || "Zu erledigen"}
                        </Badge>
                      </div>
                    </div>
                    {task.description && (
                      <p className="text-sm text-gray-600 mb-2">
                        {task.description}
                      </p>
                    )}
                    <div className="flex items-center justify-between text-sm text-gray-500">
                      <div className="flex items-center space-x-4">
                        {task.assignee && (
                          <div className="flex items-center space-x-1">
                            <Users className="w-4 h-4" />
                            <span>{task.assignee}</span>
