      {/* Quick Actions */}
      <Card className="border border-gray-200">
        <CardHeader>
          <CardTitle>Schnellaktionen</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <Button
              onClick={() => {
                createTaskMutation.mutate({
                  title: "Neuer Kundenbesichtigung",
                  description: "Besichtigung f√ºr neue Immobilie planen",
                  priority: "Hoch",
                  type: "Immobilien",
                });
              }}
              disabled={
                createTaskMutation.isPending || !connectionStatus?.success
              }
              className="h-20 flex-col space-y-2"
            >
              <Calendar className="w-6 h-6" />
              <span>Besichtigung planen</span>
            </Button>

            <Button
              variant="outline"
              onClick={() => {
                createTaskMutation.mutate({
                  title: "Newsletter vorbereiten",
                  description: "Monatlicher Newsletter mit neuen Immobilien",
                  priority: "Mittel",
                  type: "Marketing",
                });
              }}
              disabled={
                createTaskMutation.isPending || !connectionStatus?.success
              }
              className="h-20 flex-col space-y-2"
