              compass: showCompass,
              northOffset: 0,
              showZoomCtrl: true,
              keyboardZoom: true,
              mouseZoom: true,
              showFullscreenCtrl: false,
              autoRotate: isPlaying ? -2 : 0,
            });

            // Warten bis der Viewer vollst√§ndig geladen ist
            viewer.on("load", () => {
              setPannellumViewer(viewer);
            });
          } catch (error) {
            console.error("Error creating 360¬∞ viewer:", error);
          }
        };

        // Validate image URL
        const imageUrl = scenes[currentScene].image;
