          setIsVisible(true);
        }
      },
      { threshold: 0.1 },
    );

    const element = document.getElementById("properties-nav");
    if (element) {
      observer.observe(element);
    }

    return () => {
      if (element) {
        observer.unobserve(element);
      }
    };
