// Real Auth hook with backend API calls
export function useAuth() {
  return useQuery({
    queryKey: ['user'],
    queryFn: async (): Promise<AuthResponse | null> => {
      // Check session storage first
      if (typeof window !== 'undefined') {
        const stored = sessionStorage.getItem('admin_user');
        if (stored) {
          try {
            return JSON.parse(stored);
          } catch {
            sessionStorage.removeItem('admin_user');
          }
        }
      }

      // Verify with server
      try {
        const response = await fetch('/api/auth/me', {
          credentials: 'include',
        });

        if (response.ok) {
          const userData = await response.json();
          if (typeof window !== 'undefined') {
            sessionStorage.setItem('admin_user', JSON.stringify(userData));
          }
          return userData;
        }
      } catch (error) {
        console.error('Auth check failed:', error);
      }

      return null;
    },
    staleTime: 1000 * 60 * 5, // 5 minutes
    retry: false,
  });
}

// Mutation hooks
export function useLogin() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async (credentials: LoginCredentials): Promise<AuthResponse> => {
      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(credentials),
        credentials: 'include', // Important for session cookies
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || 'Login fehlgeschlagen');
      }

      const userData = await response.json();

      // Store in sessionStorage for persistence
      if (typeof window !== 'undefined') {
        sessionStorage.setItem('admin_user', JSON.stringify(userData));
      }

      return userData;
    },
    onSuccess: (userData) => {
      // Invalidate any cached queries that depend on auth state
      queryClient.invalidateQueries({ queryKey: ['user'] });
      queryClient.invalidateQueries({ queryKey: ['auth'] });
    },
    onError: (error) => {
      console.error('Login error:', error);
      // Clear any stored auth data on error
      if (typeof window !== 'undefined') {
        sessionStorage.removeItem('admin_user');
      }
    }
  });
}

