/**
 * ImmoXX Production Build Script
 * Konsolidiertes Build-Script f√ºr Client & Server
 */

import { execSync } from 'child_process';
import fs from 'fs';

console.log('üöÄ ImmoXX Production Build');
console.log('===========================\n');

try {
  // 1. Clean previous builds
  console.log('üßπ Cleaning previous builds...');
  if (fs.existsSync('dist')) {
    execSync('rm -rf dist', { stdio: 'inherit' });
  }
  fs.mkdirSync('dist', { recursive: true });
  fs.mkdirSync('dist/public', { recursive: true });

  // 2. Build client with Vite
  console.log('\nüì¶ Building client with Vite...');
  execSync('vite build', { stdio: 'inherit' });
  console.log('‚úÖ Client build completed');

  // 3. Build server with TypeScript (with tsx fallback)
  console.log('\nüîß Building server...');
  let serverCompiled = false;

  try {
    execSync('tsc -p tsconfig.prod.json', { stdio: 'inherit' });
    serverCompiled = true;
