        }

        const fileContent = fs.readFileSync(req.file.path, 'utf-8');

        // Parse CSV with Papa Parse
        const parseResult = Papa.parse(fileContent, {
          header: true,
          skipEmptyLines: true,
          transformHeader: (header) => header.trim()
