// =====================================================
// GET /api/crm/leads - Get all leads with filters
// =====================================================
router.get("/", async (req: Request, res: Response) => {
  try {
    const {
      status,
      temperature,
      assigned_to,
      pipeline_stage,
      source,
      search,
      limit,
      offset,
    } = req.query;

