  area: z.union([
    z.number(),
    z.string(),
    z.null(),
    z.undefined()
  ]).optional().transform(safeParseInteger),
  rooms: z.union([
    z.number(),
    z.string(),
    z.null(),
    z.undefined()
  ]).optional().transform(safeParseInteger),
  bathrooms: z.union([
