      });

      return response.data.items || [];
    }, connection, null, 'fetch');
  }

  /**
   * Execute calendar operation with retry logic for token refresh
   */
  private async executeWithRetry<T>(
    operation: (connection: CalendarConnection) => Promise<T>,
    connection: CalendarConnection,
    appointmentId: string | null,
    operationType: string
  ): Promise<T> {
    let lastError: Error | null = null;
    let currentConnection = connection;

    for (let attempt = 1; attempt <= this.MAX_RETRY_ATTEMPTS; attempt++) {
      try {
        // Set credentials with proactive refresh
        currentConnection = await this.setCredentials(currentConnection);

        // Execute the operation
        return await operation(currentConnection);
      } catch (error) {
        lastError = error instanceof Error ? error : new Error(String(error));
        const errorMessage = getErrorMessage(error);

        console.error(`Calendar operation '${operationType}' failed (attempt ${attempt}/${this.MAX_RETRY_ATTEMPTS}):`, error);

        // Check if this is an authentication error
        if (this.isAuthenticationError(error)) {
          if (attempt < this.MAX_RETRY_ATTEMPTS) {
            console.log(`Authentication error detected, attempting token refresh (attempt ${attempt})`);

            try {
              // Force token refresh on authentication errors
              currentConnection = await this.refreshAccessToken(currentConnection);
              console.log(`Token refreshed successfully, retrying operation`);

              // Wait before retry
              await this.delay(this.RETRY_DELAY_MS * attempt);
              continue;
            } catch (refreshError) {
              console.error(`Token refresh failed on attempt ${attempt}:`, refreshError);

              // If refresh fails and this is our last attempt, or refresh token is invalid, fail immediately
              if (getErrorMessage(refreshError).includes('invalid_grant') || attempt === this.MAX_RETRY_ATTEMPTS) {
                // Update appointment status if provided
                if (appointmentId) {
                  await this.updateAppointmentSyncError(appointmentId, `Authentication failed: ${getErrorMessage(refreshError)}`);
                }

                // Log failure
                await this.logSyncOperation(
                  currentConnection.id,
                  appointmentId,
                  operationType as any,
                  'crm_to_calendar',
                  'error',
                  { attempt, operationType },
                  `Token refresh failed: ${getErrorMessage(refreshError)}`,
                  { originalError: errorMessage, refreshError: getErrorMessage(refreshError) }
                );

                throw new Error(`Token refresh failed - user re-authentication required: ${getErrorMessage(refreshError)}`);
              }
            }
          }
        } else {
          // Non-authentication error
          if (attempt < this.MAX_RETRY_ATTEMPTS) {
            console.log(`Retrying operation '${operationType}' due to non-auth error (attempt ${attempt})`);
            await this.delay(this.RETRY_DELAY_MS * attempt);
            continue;
          }
        }
      }
    }

    // All retries exhausted
    const finalError = lastError || new Error('Unknown error after all retries');

    // Update appointment status if provided
    if (appointmentId) {
      await this.updateAppointmentSyncError(appointmentId, finalError.message);
    }

    // Log final failure
    await this.logSyncOperation(
      currentConnection.id,
      appointmentId,
      operationType as any,
      'crm_to_calendar',
      'error',
      { attempts: this.MAX_RETRY_ATTEMPTS, operationType },
      `Operation failed after ${this.MAX_RETRY_ATTEMPTS} attempts: ${finalError.message}`,
      { error: finalError.message }
    );

    throw finalError;
  }

  /**
   * Check if error is an authentication/authorization error
   */
  private isAuthenticationError(error: any): boolean {
    const errorMessage = (error.message || '').toLowerCase();
    const statusCode = error.status || error.statusCode || error.code;

    return (
      statusCode === 401 ||
      statusCode === 403 ||
      errorMessage.includes('unauthorized') ||
      errorMessage.includes('forbidden') ||
      errorMessage.includes('invalid credentials') ||
      errorMessage.includes('authentication') ||
      errorMessage.includes('token') ||
      errorMessage.includes('invalid_token') ||
      errorMessage.includes('token_expired')
    );
  }

  /**
   * Update appointment sync error status
   */
  private async updateAppointmentSyncError(appointmentId: string, error: string): Promise<void> {
    try {
      await db
        .update(schema.appointments)
        .set({
          calendarSyncStatus: 'error',
          calendarSyncError: error,
          lastCalendarSync: new Date(),
        })
        .where(eq(schema.appointments.id, appointmentId));
    } catch (dbError) {
      console.error('Failed to update appointment sync error:', dbError);
    }
  }

  /**
   * Delay utility for retries
   */
  private delay(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  /**
   * Check if token is nearing expiry
   */
  isTokenNearingExpiry(connection: CalendarConnection, bufferMinutes: number = 5): boolean {
    if (!connection.tokenExpiresAt) {
      return true; // If no expiry date, assume it needs refresh
    }

    const now = new Date();
    const expiryThreshold = new Date(now.getTime() + (bufferMinutes * 60 * 1000));

    return connection.tokenExpiresAt <= expiryThreshold;
  }

  /**
   * Get token health status
   */
  getTokenHealthStatus(connection: CalendarConnection): {
    status: 'healthy' | 'expiring_soon' | 'expired' | 'invalid';
    expiresAt?: Date;
    minutesToExpiry?: number;
    needsRefresh: boolean;
    canRefresh: boolean;
  } {
    const now = new Date();
    const hasRefreshToken = Boolean(connection.refreshToken);

    if (!connection.tokenExpiresAt) {
      return {
        status: 'invalid',
        needsRefresh: true,
        canRefresh: hasRefreshToken
      };
    }

    const minutesToExpiry = Math.floor((connection.tokenExpiresAt.getTime() - now.getTime()) / (1000 * 60));

    let status: 'healthy' | 'expiring_soon' | 'expired' | 'invalid';
    let needsRefresh = false;

    if (minutesToExpiry <= 0) {
      status = 'expired';
      needsRefresh = true;
    } else if (minutesToExpiry <= this.TOKEN_REFRESH_BUFFER_MINUTES) {
      status = 'expiring_soon';
      needsRefresh = true;
    } else {
      status = 'healthy';
    }

    return {
      status,
      expiresAt: connection.tokenExpiresAt,
      minutesToExpiry: Math.max(0, minutesToExpiry),
      needsRefresh,
      canRefresh: hasRefreshToken
    };
  }

  /**
   * Build event description from appointment data
   */
  private buildEventDescription(appointment: Appointment): string {
    let description = '';

    if (appointment.notes) {
      description += `${appointment.notes}\n\n`;
    }

    description += `Terminart: ${this.getTypeDisplayName(appointment.type)}\n`;
    description += `Status: ${this.getStatusDisplayName(appointment.status)}\n`;

    if (appointment.customerId) {
      description += `Kunde: ${appointment.customerId}\n`;
    }

    if (appointment.propertyId) {
      description += `Immobilie: ${appointment.propertyId}\n`;
    }

    if (appointment.preparation) {
      description += `\nVorbereitung:\n${appointment.preparation}`;
    }

    description += '\n\n--- Automatisch erstellt von Bodensee Immobilien M√ºller CRM ---';

    return description;
  }

  /**
   * Get display name for appointment type
   */
  private getTypeDisplayName(type: string): string {
    const typeMap: { [key: string]: string } = {
      property_viewing: 'Besichtigung',
      consultation: 'Beratung',
      valuation: 'Bewertung',
      contract_signing: 'Vertragsunterzeichnung',
      follow_up: 'Nachfassgespr√§ch',
    };
    return typeMap[type] || type;
  }

  /**
   * Get display name for appointment status
   */
  private getStatusDisplayName(status: string): string {
    const statusMap: { [key: string]: string } = {
      scheduled: 'Geplant',
      confirmed: 'Best√§tigt',
      completed: 'Abgeschlossen',
      cancelled: 'Abgesagt',
      rescheduled: 'Verschoben',
      no_show: 'Nicht erschienen',
    };
    return statusMap[status] || status;
  }

  /**
   * Log sync operation
   */
  private async logSyncOperation(
    connectionId: string | null,
    appointmentId: string | null,
    operation: 'create' | 'update' | 'delete' | 'sync',
    direction: 'crm_to_calendar' | 'calendar_to_crm',
    status: 'success' | 'error' | 'skipped',
    dataSnapshot: any = null,
    errorMessage?: string,
    errorDetails?: any
  ): Promise<void> {
    try {
      const logData: InsertCalendarSyncLog = {
        connectionId,
        appointmentId,
        operation,
        direction,
        status,
        dataSnapshot,
        errorMessage,
        errorDetails,
        completedAt: new Date(),
        duration: 0, // TODO: Add actual timing
      };

      await db.insert(schema.calendarSyncLogs).values(logData);
    } catch (error) {
      console.error('Failed to log sync operation:', error);
    }
  }

  /**
   * Test connection by making a simple API call with retry logic
   */
  async testConnection(connection: CalendarConnection): Promise<boolean> {
    try {
      await this.executeWithRetry(async (updatedConnection) => {
        // Make a simple call to verify connection
        await this.calendar.calendarList.get({
          calendarId: updatedConnection.calendarId!,
        });

        // Update connection status on success
        await db
          .update(schema.calendarConnections)
          .set({
            syncStatus: 'connected',
            syncError: null,
            lastSyncAt: new Date(),
            updatedAt: new Date(),
          })
          .where(eq(schema.calendarConnections.id, updatedConnection.id));

        return true;
      }, connection, null, 'test');

      return true;
    } catch (error) {
      console.error('Connection test failed:', error);

      // Update connection status on failure
      await db
        .update(schema.calendarConnections)
        .set({
          syncStatus: 'error',
          syncError: `Connection test failed: ${getErrorMessage(error)}`,
          updatedAt: new Date(),
        })
        .where(eq(schema.calendarConnections.id, connection.id));

      return false;
    }
  }

  /**
   * Validate OAuth state for CSRF protection
   */
  private validateOAuthState(
    state: string, 
    session: any, 
    expectedProvider: 'google' | 'apple' | 'outlook'
  ): { agentId: string } {
    if (!session.oauthStates || !session.oauthStates[state]) {
      throw new Error('Invalid or expired OAuth state parameter');
    }

    const stateData = session.oauthStates[state];

    // Check if state is expired
    if (Date.now() > stateData.expiresAt) {
      delete session.oauthStates[state];
      throw new Error('OAuth state parameter has expired');
    }

    // Verify provider matches
    if (stateData.provider !== expectedProvider) {
      throw new Error('OAuth state provider mismatch');
    }

    // Clean up used state
    delete session.oauthStates[state];

    return { agentId: stateData.agentId };
  }

  /**
   * Clean up expired OAuth states from session
   */
  private cleanupExpiredStates(session: any): void {
    if (!session.oauthStates) {
      return;
    }

    const now = Date.now();
    Object.keys(session.oauthStates).forEach(state => {
      if (session.oauthStates[state].expiresAt < now) {
        delete session.oauthStates[state];
      }
    });
  }
}

export const googleCalendarService = new GoogleCalendarService();

import { db } from "../db.js";
import { rateLimitEntries } from "@shared/schema";
import { eq, and, sql, lt } from "drizzle-orm";

interface RateLimitRecord {
  count: number;
  resetTime: number;
  firstAttemptTime?: number;
  blocked?: boolean;
}

interface RateLimitResult {
  allowed: boolean;
  resetTime?: number;
  retryAfter?: number;
  currentCount?: number;
}

// FAIL-SAFE: In-memory fallback storage for DB failures
class InMemoryRateLimitFallback {
  private readonly storage = new Map<string, RateLimitRecord>();
  private readonly maxEntries = 10000; // Bounded storage to prevent memory leaks

  private getKey(clientId: string, limitType: string): string {
    return `${clientId}:${limitType}`;
  }

  get(clientId: string, limitType: string): RateLimitRecord | null {
    const key = this.getKey(clientId, limitType);
    const record = this.storage.get(key);

    if (!record) return null;

    // Check if expired
    if (Date.now() >= record.resetTime) {
      this.storage.delete(key);
      return null;
    }

    return record;
  }

  set(clientId: string, limitType: string, record: RateLimitRecord): void {
    // Optimized bounded storage management
    if (this.storage.size >= this.maxEntries) {
      const now = Date.now();
      const expiredKeys: string[] = [];
      let checkedCount = 0;

      // More efficient cleanup: use iterator with early termination
      for (const [key, value] of this.storage.entries()) {
        if (checkedCount >= 500) break; // Limit work per operation

        if (now >= value.resetTime) {
          expiredKeys.push(key);
        }
        checkedCount++;

        if (expiredKeys.length >= 200) break; // Batch cleanup limit
      }

      // Batch delete expired entries
      expiredKeys.forEach(key => this.storage.delete(key));

      // If still at capacity, remove oldest entries more efficiently
      if (this.storage.size >= this.maxEntries) {
        const oldestKeys: string[] = [];
        let oldestTime = Infinity;

        for (const [key, value] of this.storage.entries()) {
          if (value.resetTime < oldestTime) {
            oldestTime = value.resetTime;
            oldestKeys.unshift(key); // Add to front
          }
          if (oldestKeys.length > 100) {
            oldestKeys.pop(); // Remove from back
          }
        }

        // Remove oldest entries
        oldestKeys.slice(0, 50).forEach(key => this.storage.delete(key));
      }
    }

    const key = this.getKey(clientId, limitType);
    this.storage.set(key, record);
  }

  cleanup(): number {
    const now = Date.now();
    let removed = 0;

    // Optimized cleanup with early termination and batching
    const entriesToDelete: string[] = [];

    for (const [key, record] of this.storage.entries()) {
      if (now >= record.resetTime) {
        entriesToDelete.push(key);
        removed++;

        // Batch delete to avoid excessive iteration
        if (entriesToDelete.length >= 100) {
          break;
        }
      }
    }

    // Batch delete expired entries
    entriesToDelete.forEach(key => this.storage.delete(key));

    return removed;
  }

  getSize(): number {
    return this.storage.size;
  }
}

const fallbackStorage = new InMemoryRateLimitFallback();

export class RateLimitingService {

  /**
   * Check and increment rate limit for login attempts
   * ATOMIC IMPLEMENTATION: Eliminates race conditions using upsert with atomic increment
   * FAIL-SAFE: Falls back to in-memory storage on DB errors
   */
  static async checkLoginRateLimit(clientId: string): Promise<RateLimitResult> {
    const now = Date.now();
    const shortWindowMs = 5 * 60 * 1000; // 5 minutes
    const longWindowMs = 60 * 60 * 1000; // 1 hour
    const maxAttemptsShort = 5; // Max 5 attempts per 5 minutes
    const maxAttemptsLong = 10; // Max 10 attempts per hour

    try {
      // TEMPORARILY DISABLED: Database rate limiting has schema issues
      // Falling back to in-memory only for now
      throw new Error('Database rate limiting temporarily disabled - using in-memory fallback');

      // TRY DATABASE FIRST
      return await db.transaction(async (tx) => {
        const currentTime = new Date(now);
        const resetTimeShort = new Date(now + shortWindowMs);
        const resetTimeLong = new Date(now + longWindowMs);

        // ATOMIC OPERATION: Insert or update with atomic increment and window reset logic
        const [result] = await tx
          .insert(rateLimitEntries)
          .values({
            identifier: clientId,
            endpoint: 'login',
            count: 1,
            resetTime: resetTimeShort,
            firstAttemptTime: currentTime,
            blocked: false,
          })
          .onConflictDoUpdate({
            target: [rateLimitEntries.identifier, rateLimitEntries.endpoint], // Uses the unique constraint
            set: {
              // ATOMIC INCREMENT with window reset logic in single query
              count: sql`CASE 
                WHEN ${rateLimitEntries.resetTime} <= ${currentTime} THEN 1 
                ELSE ${rateLimitEntries.count} + 1 
              END`,
              resetTime: sql`CASE
                WHEN ${rateLimitEntries.resetTime} <= ${currentTime} THEN ${resetTimeShort}
                WHEN ${rateLimitEntries.count} + 1 > ${maxAttemptsShort} AND
                     (strftime('%s', ${currentTime}) * 1000 - strftime('%s', COALESCE(${rateLimitEntries.firstAttemptTime}, ${rateLimitEntries.createdAt})) * 1000) < 3600000 THEN ${resetTimeLong}
                ELSE ${rateLimitEntries.resetTime}
              END`,
              firstAttemptTime: sql`CASE
                WHEN ${rateLimitEntries.resetTime} <= ${currentTime} THEN ${currentTime}
                ELSE COALESCE(${rateLimitEntries.firstAttemptTime}, ${rateLimitEntries.createdAt})
              END`,
              blocked: sql`CASE
                WHEN ${rateLimitEntries.resetTime} <= ${currentTime} THEN false
                WHEN ${rateLimitEntries.count} + 1 > ${maxAttemptsLong} AND
                     (strftime('%s', ${currentTime}) * 1000 - strftime('%s', COALESCE(${rateLimitEntries.firstAttemptTime}, ${rateLimitEntries.createdAt})) * 1000) < 3600000 THEN true
                ELSE ${rateLimitEntries.blocked}
              END`,
              updatedAt: currentTime,
            },
          })
          .returning();

        // Check the result for rate limiting decision
        const isBlocked = result.blocked && now < new Date(result.resetTime).getTime();
        const withinShortLimit = result.count <= maxAttemptsShort;
        const timeSinceFirst = now - new Date(result.firstAttemptTime || result.createdAt!).getTime();
        const withinLongWindow = timeSinceFirst < longWindowMs;
        const exceedsLongLimit = result.count > maxAttemptsLong;

        if (isBlocked || (withinLongWindow && exceedsLongLimit)) {
          return {
            allowed: false,
            resetTime: new Date(result.resetTime).getTime(),
            retryAfter: Math.ceil((new Date(result.resetTime).getTime() - now) / 1000),
            currentCount: result.count,
          };
        }

        return { allowed: true, currentCount: result.count };
      });
    } catch (error) {
      // FAIL-SAFE: Fall back to in-memory storage on DB errors
      console.error('üîí Database rate limiting failed, using in-memory fallback:', error);
      return this.checkLoginRateLimitFallback(clientId, now, shortWindowMs, longWindowMs, maxAttemptsShort, maxAttemptsLong);
    }
  }

  /**
   * FAIL-SAFE: In-memory fallback for login rate limiting
   */
  private static checkLoginRateLimitFallback(
    clientId: string, 
    now: number, 
    shortWindowMs: number, 
    longWindowMs: number, 
    maxAttemptsShort: number, 
    maxAttemptsLong: number
  ): RateLimitResult {
    const existing = fallbackStorage.get(clientId, 'login');

    if (!existing || now >= existing.resetTime) {
      // New window or expired - start fresh
      const record: RateLimitRecord = {
        count: 1,
        resetTime: now + shortWindowMs,
        firstAttemptTime: now,
        blocked: false,
      };
      fallbackStorage.set(clientId, 'login', record);
      return { allowed: true, currentCount: 1 };
    }

    // Check if currently blocked
    if (existing.blocked && now < existing.resetTime) {
      return {
        allowed: false,
        resetTime: existing.resetTime,
        retryAfter: Math.ceil((existing.resetTime - now) / 1000),
        currentCount: existing.count,
      };
    }

    const newCount = existing.count + 1;
    const timeSinceFirst = now - (existing.firstAttemptTime || now);

    // Check limits and apply blocking logic
    if (newCount <= maxAttemptsShort) {
      // Within short window limits
      existing.count = newCount;
      fallbackStorage.set(clientId, 'login', existing);
      return { allowed: true, currentCount: newCount };
    } else if (timeSinceFirst < longWindowMs && newCount > maxAttemptsLong) {
      // Exceeded long window limits - block
      existing.count = newCount;
      existing.resetTime = now + longWindowMs;
      existing.blocked = true;
      fallbackStorage.set(clientId, 'login', existing);

      return {
        allowed: false,
        resetTime: existing.resetTime,
        retryAfter: Math.ceil((existing.resetTime - now) / 1000),
        currentCount: newCount,
      };
    } else {
      // Extend window
      existing.count = newCount;
      existing.resetTime = now + shortWindowMs;
      fallbackStorage.set(clientId, 'login', existing);
      return { allowed: true, currentCount: newCount };
    }
  }

  /**
   * Check and increment rate limit for admin operations
   * ATOMIC IMPLEMENTATION: Eliminates race conditions using upsert with atomic increment
   * FAIL-SAFE: Falls back to in-memory storage on DB errors
   */
  static async checkAdminRateLimit(clientId: string): Promise<RateLimitResult> {
    const now = Date.now();
    const windowMs = 15 * 60 * 1000; // 15 minutes
    const maxRequests = 10; // Max 10 admin operations per 15 minutes

    try {
      // TRY DATABASE FIRST
      return await db.transaction(async (tx) => {
        const currentTime = new Date(now);
        const resetTime = new Date(now + windowMs);

        // ATOMIC OPERATION: Insert or update with atomic increment and window reset logic
        const [result] = await tx
          .insert(rateLimitEntries)
          .values({
            identifier: clientId,
            endpoint: 'admin',
            count: 1,
            resetTime: resetTime,
            firstAttemptTime: currentTime,
            blocked: false,
          })
          .onConflictDoUpdate({
            target: [rateLimitEntries.identifier, rateLimitEntries.endpoint], // Uses the unique constraint
            set: {
              // ATOMIC INCREMENT with window reset logic in single query
              count: sql`CASE 
                WHEN ${rateLimitEntries.resetTime} <= ${currentTime} THEN 1 
                ELSE ${rateLimitEntries.count} + 1 
              END`,
              resetTime: sql`CASE 
                WHEN ${rateLimitEntries.resetTime} <= ${currentTime} THEN ${resetTime}
                ELSE ${rateLimitEntries.resetTime}
              END`,
              firstAttemptTime: sql`CASE 
                WHEN ${rateLimitEntries.resetTime} <= ${currentTime} THEN ${currentTime}
                ELSE COALESCE(${rateLimitEntries.firstAttemptTime}, ${rateLimitEntries.createdAt})
              END`,
              blocked: sql`CASE 
                WHEN ${rateLimitEntries.resetTime} <= ${currentTime} THEN false
                ELSE ${rateLimitEntries.blocked}
              END`,
              updatedAt: currentTime,
            },
          })
          .returning();

        // Check the result for rate limiting decision
        const isExpired = now >= new Date(result.resetTime).getTime();
        const exceedsLimit = result.count > maxRequests;

        if (!isExpired && exceedsLimit) {
          return {
            allowed: false,
            resetTime: new Date(result.resetTime).getTime(),
            retryAfter: Math.ceil((new Date(result.resetTime).getTime() - now) / 1000),
            currentCount: result.count,
          };
        }

        return { allowed: true, currentCount: result.count };
      });
    } catch (error) {
      // FAIL-SAFE: Fall back to in-memory storage on DB errors
      console.error('üîí Database admin rate limiting failed, using in-memory fallback:', error);
      return this.checkAdminRateLimitFallback(clientId, now, windowMs, maxRequests);
    }
  }

  /**
   * FAIL-SAFE: In-memory fallback for admin rate limiting
   */
  private static checkAdminRateLimitFallback(
    clientId: string, 
    now: number, 
    windowMs: number, 
    maxRequests: number
  ): RateLimitResult {
    const existing = fallbackStorage.get(clientId, 'admin');

    if (!existing || now >= existing.resetTime) {
      // New window or expired - start fresh
      const record: RateLimitRecord = {
        count: 1,
        resetTime: now + windowMs,
        firstAttemptTime: now,
        blocked: false,
      };
      fallbackStorage.set(clientId, 'admin', record);
      return { allowed: true, currentCount: 1 };
    }

    const newCount = existing.count + 1;

    if (newCount <= maxRequests) {
      // Within limits
      existing.count = newCount;
      fallbackStorage.set(clientId, 'admin', existing);
      return { allowed: true, currentCount: newCount };
    }

    // Rate limit exceeded
    return {
      allowed: false,
      resetTime: existing.resetTime,
      retryAfter: Math.ceil((existing.resetTime - now) / 1000),
      currentCount: existing.count,
    };
  }

  /**
   * Clean up expired rate limit entries to prevent memory leaks
   * OPTIMIZED: Uses efficient indexed deletes for better performance
   */
  static async cleanupExpiredEntries(tx: any, limitType?: string): Promise<void> {
    const now = new Date();

    if (limitType) {
      // Uses limit_type_reset_idx index for efficient lookup
      await tx
        .delete(rateLimitEntries)
        .where(
          and(
            eq(rateLimitEntries.limitType, limitType),
            lt(rateLimitEntries.resetTime, now)
          )
        );
    } else {
      // Uses limit_type_reset_idx index for efficient cleanup across all types
      await tx
        .delete(rateLimitEntries)
        .where(lt(rateLimitEntries.resetTime, now));
    }
  }

  /**
   * Periodic cleanup to run in background
   * ENHANCED: Includes fail-safe in-memory cleanup
   */
  static async performPeriodicCleanup(): Promise<number> {
    let dbCleaned = 0;
    let memoryCleaned = 0;

    try {
      // Clean up database entries
      const now = new Date();
      const result = await db
        .delete(rateLimitEntries)
        .where(lt(rateLimitEntries.resetTime, now));

      dbCleaned = result.rowCount || 0;
    } catch (error) {
      console.error('üîí Database rate limiting cleanup error:', error);
    }

    try {
      // Clean up in-memory fallback storage
      memoryCleaned = fallbackStorage.cleanup();
    } catch (error) {
      console.error('üîí In-memory rate limiting cleanup error:', error);
    }

    const totalCleaned = dbCleaned + memoryCleaned;
    if (totalCleaned > 0) {
      console.log(`üîí Rate limiting cleanup: DB: ${dbCleaned}, Memory: ${memoryCleaned}, Total: ${totalCleaned}`);
    }

    return totalCleaned;
  }

  /**
   * Get rate limit status for monitoring
   */
  static async getRateLimitStatus(clientId: string, limitType: string): Promise<RateLimitRecord | null> {
    try {
      const record = await db
        .select()
        .from(rateLimitEntries)
        .where(
          and(
            eq(rateLimitEntries.clientId, clientId),
            eq(rateLimitEntries.limitType, limitType)
          )
        )
        .limit(1);

      if (!record[0]) return null;

      return {
        count: record[0].count,
        resetTime: new Date(record[0].resetTime).getTime(),
        firstAttemptTime: record[0].firstAttemptTime ? new Date(record[0].firstAttemptTime).getTime() : undefined,
        blocked: record[0].blocked || false,
      };
    } catch (error) {
      console.error('üîí Rate limiting status check error:', error);
      return null;
    }
  }

  /**
   * Reset rate limit for a client (admin operation)
   */
  static async resetRateLimit(clientId: string, limitType?: string): Promise<boolean> {
    try {
      if (limitType) {
        await db
          .delete(rateLimitEntries)
          .where(
            and(
              eq(rateLimitEntries.clientId, clientId),
              eq(rateLimitEntries.limitType, limitType)
            )
          );
      } else {
        await db
          .delete(rateLimitEntries)
          .where(eq(rateLimitEntries.clientId, clientId));
      }
      return true;
    } catch (error) {
      console.error('üîí Rate limiting reset error:', error);
      return false;
    }
  }
}

// Set up periodic cleanup - runs every 10 minutes
let cleanupInterval: NodeJS.Timeout | null = null;

export const startRateLimitCleanup = () => {
  if (cleanupInterval) return; // Already started

  cleanupInterval = setInterval(async () => {
    const cleaned = await RateLimitingService.performPeriodicCleanup();
    if (cleaned > 0) {
      console.log(`üîí Rate limiting cleanup: Removed ${cleaned} expired entries`);
    }
  }, 10 * 60 * 1000); // Every 10 minutes

  console.log('üîí Rate limiting periodic cleanup started');
};

export const stopRateLimitCleanup = () => {
  if (cleanupInterval) {
    clearInterval(cleanupInterval);
    cleanupInterval = null;
    console.log('üîí Rate limiting periodic cleanup stopped');
  }
};

import { db } from '../db.js';
import * as schema from '@shared/schema';
import { eq, and, isNull, ne, lt, isNotNull } from 'drizzle-orm';
import type { CalendarConnection } from '@shared/schema';
import { calendarConnections } from '@shared/schema';
import { googleCalendarService } from './googleCalendarService.js';
import { appleCalendarService } from './appleCalendarService.js';

interface TokenMaintenanceResult {
  checked: number;
  refreshed: number;
  expired: number;
  errors: number;
  summary: {
    healthy: number;
    expiring_soon: number;
    expired: number;
    invalid: number;
  };
}

interface MaintenanceError {
  connectionId: string;
  provider: string;
  agentId: string;
  error: string;
  requiresReauth: boolean;
}

/**
 * Service for maintaining calendar authentication tokens
 * Performs proactive token refresh and health monitoring
 */
export class TokenMaintenanceService {
  private readonly TOKEN_CHECK_INTERVAL_MS = 15 * 60 * 1000; // Check every 15 minutes
  private readonly TOKEN_REFRESH_BUFFER_MINUTES = 10; // Refresh tokens 10 minutes before expiry
  private readonly BATCH_SIZE = 50; // Process connections in batches

  private maintenanceTimer: NodeJS.Timeout | null = null;
  private isRunning = false;

  constructor() {}

  /**
   * Start the background token maintenance service
   */
  start(): void {
    if (this.isRunning) {
      console.log('Token maintenance service is already running');
      return;
    }

    this.isRunning = true;
    console.log('Starting token maintenance service...');

    // Run immediately on start
    this.runMaintenance().catch(error => {
      console.error('Initial token maintenance failed:', error);
    });

    // Schedule periodic maintenance
    this.maintenanceTimer = setInterval(() => {
      this.runMaintenance().catch(error => {
        console.error('Scheduled token maintenance failed:', error);
      });
    }, this.TOKEN_CHECK_INTERVAL_MS);

    console.log(`Token maintenance service started - checking every ${this.TOKEN_CHECK_INTERVAL_MS / 60000} minutes`);
  }

  /**
   * Stop the background token maintenance service
   */
  stop(): void {
    if (!this.isRunning) {
      return;
    }

    if (this.maintenanceTimer) {
      clearInterval(this.maintenanceTimer);
      this.maintenanceTimer = null;
    }

    this.isRunning = false;
    console.log('Token maintenance service stopped');
  }

  /**
   * Run maintenance cycle
   */
  private async runMaintenance(): Promise<TokenMaintenanceResult> {
    const startTime = Date.now();
    console.log('Starting token maintenance cycle...');

    const result: TokenMaintenanceResult = {
      checked: 0,
      refreshed: 0,
      expired: 0,
      errors: 0,
      summary: {
        healthy: 0,
        expiring_soon: 0,
        expired: 0,
        invalid: 0
      }
    };

    try {
      // Get all active calendar connections
      const connections = await this.getActiveConnections();
      result.checked = connections.length;

      if (connections.length === 0) {
        console.log('No active calendar connections found');
        return result;
      }

      console.log(`Checking ${connections.length} calendar connections for token maintenance`);

      // Process connections in batches to avoid overwhelming the system
      const batches = this.createBatches(connections, this.BATCH_SIZE);
      const maintenanceErrors: MaintenanceError[] = [];

      for (let i = 0; i < batches.length; i++) {
        const batch = batches[i];
        console.log(`Processing batch ${i + 1}/${batches.length} (${batch.length} connections)`);

        const batchPromises = batch.map(connection => 
          this.maintainConnection(connection).catch(error => ({
            connectionId: connection.id,
            provider: connection.provider,
            agentId: connection.agentId,
            error: error instanceof Error ? error.message : 'Unknown error',
            requiresReauth: (error instanceof Error ? error.message : 'Unknown error').includes('re-authentication')
          }))
        );

        const batchResults = await Promise.all(batchPromises);

        // Process batch results
        for (const batchResult of batchResults) {
          if (typeof batchResult === 'string') {
            // Success - batchResult is the action taken
            if (batchResult === 'refreshed') result.refreshed++;
            else if (batchResult === 'expired') result.expired++;
          } else {
            // Error - batchResult is MaintenanceError
            result.errors++;
            maintenanceErrors.push(batchResult);
          }
        }

        // Small delay between batches to be gentle on APIs
        if (i < batches.length - 1) {
          await this.delay(1000);
        }
      }

      // Get health summary
      result.summary = await this.getTokenHealthSummary();

      // Log maintenance errors
      if (maintenanceErrors.length > 0) {
        console.error(`Token maintenance completed with ${maintenanceErrors.length} errors:`);
        maintenanceErrors.forEach(error => {
          console.error(`  - Connection ${error.connectionId} (${error.provider}): ${error.error}`);
        });

        // TODO: Send notifications for connections requiring re-authentication
        const reauthRequired = maintenanceErrors.filter(e => e.requiresReauth);
        if (reauthRequired.length > 0) {
          await this.notifyReauthRequired(reauthRequired);
        }
      }

      const duration = Date.now() - startTime;
      console.log(`Token maintenance cycle completed in ${duration}ms:`, {
        checked: result.checked,
        refreshed: result.refreshed,
        expired: result.expired,
        errors: result.errors,
        summary: result.summary
      });

      return result;
    } catch (error) {
      console.error('Token maintenance cycle failed:', error);
      throw error;
    }
  }

  /**
   * Maintain a single calendar connection
   */
  private async maintainConnection(connection: CalendarConnection): Promise<string> {
    try {
      // Get token health status
      let healthStatus;
      if (connection.provider === 'google') {
        healthStatus = googleCalendarService.getTokenHealthStatus(connection);
      } else if (connection.provider === 'apple') {
        // TODO: Implement for Apple when available
        return 'skipped';
      } else {
        return 'skipped';
      }

      // Handle based on token status
      if (healthStatus.status === 'expired' || healthStatus.needsRefresh) {
        if (!healthStatus.canRefresh) {
          // Mark connection as requiring re-authentication
          await this.markConnectionExpired(connection, 'Refresh token not available - re-authentication required');
          throw new Error(`Connection ${connection.id} requires re-authentication: no refresh token available`);
        }

        // Attempt token refresh
        if (connection.provider === 'google') {
          await googleCalendarService.refreshAccessToken(connection);
          console.log(`Refreshed token for Google connection ${connection.id} (agent: ${connection.agentId})`);
          return 'refreshed';
        }
      } else if (healthStatus.status === 'healthy') {
        // Update last sync time to indicate connection was checked
        await db
          .update(calendarConnections)
          .set({
            lastSyncAt: new Date(),
            updatedAt: new Date()
          })
          .where(eq(calendarConnections.id, connection.id));

        return 'healthy';
      }

      return 'no_action';
    } catch (error) {
      console.error(`Token maintenance failed for connection ${connection.id}:`, error);

      // Update connection with error status
      const errorMessage = error instanceof Error ? error.message : String(error);
      await this.markConnectionError(connection, errorMessage);

      throw error;
    }
  }

  /**
   * Get all active calendar connections that need token maintenance
   */
  private async getActiveConnections(): Promise<CalendarConnection[]> {
    try {
      return await db
        .select()
        .from(calendarConnections)
        .where(
          and(
            eq(calendarConnections.isActive, true),
            ne(calendarConnections.syncStatus, 'disconnected'),
            isNotNull(calendarConnections.accessToken)
          )
        );
    } catch (error) {
      console.log('No calendar connections table found, returning empty array');
      return [];
    }
  }

  /**
   * Get connections that need token refresh soon
   */
  async getConnectionsNeedingRefresh(bufferMinutes: number = 10): Promise<CalendarConnection[]> {
    const refreshThreshold = new Date(Date.now() + (bufferMinutes * 60 * 1000));

    return await db
      .select()
      .from(calendarConnections)
      .where(
        and(
          eq(calendarConnections.isActive, true),
          ne(calendarConnections.syncStatus, 'disconnected'),
          ne(calendarConnections.syncStatus, 'expired'),
          isNotNull(calendarConnections.accessToken),
          isNotNull(calendarConnections.refreshToken),
          lt(calendarConnections.tokenExpiresAt, refreshThreshold)
        )
      );
  }

  /**
   * Get expired connections
   */
  async getExpiredConnections(): Promise<CalendarConnection[]> {
    const now = new Date();

    return await db
      .select()
      .from(calendarConnections)
      .where(
        and(
          eq(calendarConnections.isActive, true),
          ne(calendarConnections.syncStatus, 'disconnected'),
          isNotNull(calendarConnections.accessToken),
          lt(calendarConnections.tokenExpiresAt, now)
        )
      );
  }

  /**
   * Get token health summary across all connections
   */
  async getTokenHealthSummary(): Promise<{
    healthy: number;
    expiring_soon: number;
    expired: number;
    invalid: number;
  }> {
    const connections = await this.getActiveConnections();
    const summary = {
      healthy: 0,
      expiring_soon: 0,
      expired: 0,
      invalid: 0
    };

    for (const connection of connections) {
      try {
        let healthStatus;
        if (connection.provider === 'google') {
          healthStatus = googleCalendarService.getTokenHealthStatus(connection);
        } else {
          continue; // Skip non-Google connections for now
        }

        summary[healthStatus.status]++;
      } catch (error) {
        summary.invalid++;
      }
    }

    return summary;
  }

  /**
   * Mark connection as expired and requiring re-authentication
   */
  private async markConnectionExpired(connection: CalendarConnection, reason: string): Promise<void> {
    await db
      .update(calendarConnections)
      .set({
        syncStatus: 'expired',
        syncError: reason,
        updatedAt: new Date()
      })
      .where(eq(calendarConnections.id, connection.id));
  }

  /**
   * Mark connection with error status
   */
  private async markConnectionError(connection: CalendarConnection, error: string): Promise<void> {
    await db
      .update(calendarConnections)
      .set({
        syncStatus: 'error',
        syncError: `Token maintenance failed: ${error}`,
        updatedAt: new Date()
      })
      .where(eq(calendarConnections.id, connection.id));
  }

  /**
   * Notify administrators about connections requiring re-authentication
   */
  private async notifyReauthRequired(errors: MaintenanceError[]): Promise<void> {
    // TODO: Implement notification system (email, webhook, etc.)
    console.log(`NOTIFICATION: ${errors.length} calendar connections require re-authentication:`);
    errors.forEach(error => {
      console.log(`  - Agent ${error.agentId}: ${error.provider} connection needs re-auth (${error.error})`);
    });

    // For now, just log. In production, this should send notifications to:
    // 1. The affected users/agents
    // 2. System administrators
    // 3. Monitoring systems
  }

  /**
   * Create batches from array
   */
  private createBatches<T>(items: T[], batchSize: number): T[][] {
    const batches: T[][] = [];
    for (let i = 0; i < items.length; i += batchSize) {
      batches.push(items.slice(i, i + batchSize));
    }
    return batches;
  }

  /**
   * Delay utility
   */
  private delay(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  /**
   * Get service status
   */
  getStatus(): {
    isRunning: boolean;
    checkIntervalMinutes: number;
    nextCheckIn?: number;
  } {
    return {
      isRunning: this.isRunning,
      checkIntervalMinutes: this.TOKEN_CHECK_INTERVAL_MS / 60000,
      nextCheckIn: this.maintenanceTimer ? this.TOKEN_CHECK_INTERVAL_MS : undefined
    };
  }

  /**
   * Force maintenance run (for testing/admin purposes)
   */
  async runMaintenanceNow(): Promise<TokenMaintenanceResult> {
    console.log('Running token maintenance on demand...');
    return await this.runMaintenance();
  }
}

export const tokenMaintenanceService = new TokenMaintenanceService();

